contracts/core/LiquidateVault.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "./interfaces/IPositionVault.sol";
import "./interfaces/ILiquidateVault.sol";
import "./interfaces/IPriceManager.sol";
import "./interfaces/ISettingsManager.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IOperators.sol";

import {Constants} from "../access/Constants.sol";

contract LiquidateVault is Constants, Initializable, ReentrancyGuardUpgradeable, ILiquidateVault {
    // constants
    ISettingsManager private settingsManager;
    IPriceManager private priceManager;
    IPositionVault private positionVault;
    IOperators private operators;
    IVault private vault;
    bool private isInitialized;

    // variables
    mapping(uint256 => address) public liquidateRegistrant;
    mapping(uint256 => uint256) public liquidateRegisterTime;

    event RegisterLiquidation(uint256 posId, address caller);
    event LiquidatePosition(
        uint256 indexed posId,
        address indexed account,
        uint256 indexed tokenId,
        bool isLong,
        int256[3] pnlData,
        uint256[5] posData
    );

    /* ========== INITIALIZE FUNCTIONS ========== */

    function initialize() public initializer {
        __ReentrancyGuard_init();
    }

    function init(
        IPositionVault _positionVault,
        ISettingsManager _settingsManager,
        IVault _vault,
        IPriceManager _priceManager,
        IOperators _operators
    ) external {
        require(!isInitialized, "initialized");
        require(AddressUpgradeable.isContract(address(_positionVault)), "positionVault invalid");
        require(AddressUpgradeable.isContract(address(_settingsManager)), "settingsManager invalid");
        require(AddressUpgradeable.isContract(address(_vault)), "vault invalid");
        require(AddressUpgradeable.isContract(address(_priceManager)), "priceManager is invalid");
        require(AddressUpgradeable.isContract(address(_operators)), "operators is invalid");
        positionVault = _positionVault;
        settingsManager = _settingsManager;
        vault = _vault;
        priceManager = _priceManager;
        operators = _operators;
        isInitialized = true;
    }

    /* ========== CORE FUNCTIONS ========== */

    function registerLiquidatePosition(uint256 _posId) external nonReentrant {
        (bool isPositionLiquidatable, , , ) = validateLiquidationWithPosid(_posId);
        require(isPositionLiquidatable, "position is not liquidatable");
        require(liquidateRegistrant[_posId] == address(0), "not the firstCaller");

        liquidateRegistrant[_posId] = msg.sender;
        liquidateRegisterTime[_posId] = block.timestamp;

        emit RegisterLiquidation(_posId, msg.sender);
    }

    function liquidatePosition(uint256 _posId) external nonReentrant {
        (bool isPositionLiquidatable, int256 pnl, int256 fundingFee, int256 borrowFee) = validateLiquidationWithPosid(
            _posId
        );
        require(isPositionLiquidatable, "position is not liquidatable");
        require(
            operators.getOperatorLevel(msg.sender) >= 1 ||
                (msg.sender == liquidateRegistrant[_posId] &&
                    liquidateRegisterTime[_posId] + settingsManager.liquidationPendingTime() <= block.timestamp),
            "not manager or not allowed before pendingTime"
        );

        Position memory position = positionVault.getPosition(_posId);

        (uint32 firstCallerPercent, uint32 resolverPercent) = settingsManager.bountyPercent();
        uint256 firstCallerBounty = (position.collateral * uint256(firstCallerPercent)) / BASIS_POINTS_DIVISOR;
        uint256 resolverBounty = (position.collateral * uint256(resolverPercent)) / BASIS_POINTS_DIVISOR;
        uint256 vlpBounty = position.collateral - firstCallerBounty - resolverBounty;

        if (liquidateRegistrant[_posId] == address(0)) {
            vault.takeVUSDOut(msg.sender, firstCallerBounty);
        } else {
            vault.takeVUSDOut(liquidateRegistrant[_posId], firstCallerBounty);
        }
        vault.takeVUSDOut(msg.sender, resolverBounty);
        vault.accountDeltaIntoTotalUSD(true, vlpBounty);

        settingsManager.updateFunding(position.tokenId);
        settingsManager.decreaseOpenInterest(position.tokenId, position.owner, position.isLong, position.size);

        emit LiquidatePosition(
            _posId,
            position.owner,
            position.tokenId,
            position.isLong,
            [pnl, fundingFee, borrowFee],
            [position.collateral, position.size, position.averagePrice, priceManager.getLastPrice(position.tokenId), 0]
        );
        positionVault.removeUserAlivePosition(position.owner, _posId);
    }

    /* ========== HELPER FUNCTIONS ========== */

    function validateLiquidationWithPosid(uint256 _posId) public view returns (bool, int256, int256, int256) {
        Position memory position = positionVault.getPosition(_posId);

        return
            validateLiquidation(
                position.tokenId,
                position.isLong,
                position.size,
                position.averagePrice,
                priceManager.getLastPrice(position.tokenId),
                position.lastIncreasedTime,
                position.accruedBorrowFee,
                position.fundingIndex,
                position.collateral
            );
    }

    function validateLiquidationWithPosidAndPrice(
        uint256 _posId,
        uint256 _price
    ) external view returns (bool, int256, int256, int256) {
        Position memory position = positionVault.getPosition(_posId);

        return
            validateLiquidation(
                position.tokenId,
                position.isLong,
                position.size,
                position.averagePrice,
                _price,
                position.lastIncreasedTime,
                position.accruedBorrowFee,
                position.fundingIndex,
                position.collateral
            );
    }

    function validateLiquidation(
        uint256 _tokenId,
        bool _isLong,
        uint256 _size,
        uint256 _averagePrice,
        uint256 _lastPrice,
        uint256 _lastIncreasedTime,
        uint256 _accruedBorrowFee,
        int256 _fundingIndex,
        uint256 _collateral
    ) public view returns (bool isPositionLiquidatable, int256 pnl, int256 fundingFee, int256 borrowFee) {
        require(_size > 0, "invalid position");

        (pnl, fundingFee, borrowFee) = settingsManager.getPnl(
            _tokenId,
            _isLong,
            _size,
            _averagePrice,
            _lastPrice,
            _lastIncreasedTime,
            _accruedBorrowFee,
            _fundingIndex
        );

        // position is liquidatable if pnl is negative and collateral larger than liquidateThreshold are lost
        if (
            pnl < 0 &&
            uint256(-1 * pnl) >=
            (_collateral * settingsManager.liquidateThreshold(_tokenId)) / LIQUIDATE_THRESHOLD_DIVISOR
        ) {
            isPositionLiquidatable = true;
        }
    }
}

contracts/core/MultiCall.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;
pragma experimental ABIEncoderV2;

contract Multicall {
    struct Call {
        address target;
        bytes callData;
    }

    function aggregate(Call[] memory calls) public returns (uint256 blockNumber, bytes[] memory returnData) {
        blockNumber = block.number;
        returnData = new bytes[](calls.length);
        for (uint256 i = 0; i < calls.length; i++) {
            (bool success, bytes memory ret) = calls[i].target.call(calls[i].callData);
            require(success);
            returnData[i] = ret;
        }
    }

    // Helper functions
    function getEthBalance(address addr) public view returns (uint256 balance) {
        balance = addr.balance;
    }

    function getBlockHash(uint256 blockNumber) public view returns (bytes32 blockHash) {
        blockHash = blockhash(blockNumber);
    }

    function getLastBlockHash() public view returns (bytes32 blockHash) {
        blockHash = blockhash(block.number - 1);
    }

    function getCurrentBlockTimestamp() public view returns (uint256 timestamp) {
        timestamp = block.timestamp;
    }

    function getCurrentBlockDifficulty() public view returns (uint256 difficulty) {
        difficulty = block.difficulty;
    }

    function getCurrentBlockGasLimit() public view returns (uint256 gaslimit) {
        gaslimit = block.gaslimit;
    }

    function getCurrentBlockCoinbase() public view returns (address coinbase) {
        coinbase = block.coinbase;
    }
}

contracts/core/Operators.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/Context.sol";

contract Operators is Context {
    // level 1: normal operator
    // level 2: rewards and feed manager
    // level 3: admin
    // level 4: owner
    mapping(address => uint256) operatorLevel;

    address public oldOwner;
    address public pendingOwner;

    modifier onlyOperator(uint256 level) {
        require(operatorLevel[_msgSender()] >= level, "invalid operator");
        _;
    }

    constructor() {
        operatorLevel[_msgSender()] = 4;
    }

    function setOperator(address op, uint256 level) external onlyOperator(4) {
        operatorLevel[op] = level;
    }

    function getOperatorLevel(address op) public view returns (uint256) {
        return operatorLevel[op];
    }

    function transferOwnership(address newOwner) external onlyOperator(4) {
        require(newOwner != address(0), "zero address");

        oldOwner = _msgSender();
        pendingOwner = newOwner;
    }

    function acceptOwnership() external {
        require(_msgSender() == pendingOwner, "not pendingOwner");

        operatorLevel[_msgSender()] = 4;
        operatorLevel[oldOwner] = 0;

        pendingOwner = address(0);
        oldOwner = address(0);
    }
}

contracts/core/OrderVault.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./interfaces/IOrderVault.sol";
import "./interfaces/IPositionVault.sol";
import "./interfaces/IPriceManager.sol";
import "./interfaces/ISettingsManager.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IOperators.sol";

import {Constants} from "../access/Constants.sol";
import {OrderStatus, TriggerInfo, TriggerStatus, PositionTrigger, AddPositionOrder, DecreasePositionOrder} from "./structs.sol";

contract OrderVault is Constants, Initializable, ReentrancyGuardUpgradeable, IOrderVault {
    // constants
    IPriceManager private priceManager;
    IPositionVault private positionVault;
    ISettingsManager private settingsManager;
    IVault private vault;
    IOperators private operators;
    bool private isInitialized;

    // variables
    mapping(uint256 => Order) public orders;
    mapping(uint256 => AddPositionOrder) public addPositionOrders;
    mapping(uint256 => DecreasePositionOrder) public decreasePositionOrders;

    mapping(uint256 => PositionTrigger) private triggerOrders;
    mapping(uint256 => EnumerableSetUpgradeable.UintSet) private aliveTriggerIds;

    event NewOrder(
        uint256 posId,
        address account,
        bool isLong,
        uint256 tokenId,
        uint256 positionType,
        OrderStatus orderStatus,
        uint256[] triggerData,
        address refer
    );
    event UpdateOrder(uint256 posId, uint256 positionType, OrderStatus orderStatus);
    event FinishOrder(uint256 posId, uint256 positionType, OrderStatus orderStatus);

    event AddTriggerOrders(
        uint256 posId,
        uint256 orderId,
        bool isTP,
        uint256 price,
        uint256 amountPercent,
        TriggerStatus status
    );
    event EditTriggerOrder(uint256 indexed posId, uint256 orderId, bool isTP, uint256 price, uint256 amountPercent);
    event ExecuteTriggerOrders(uint256 posId, uint256 amount, uint256 orderId, uint256 price);
    event UpdateTriggerOrderStatus(uint256 posId, uint256 orderId, TriggerStatus status);

    event AddTrailingStop(uint256 posId, uint256[] data);
    event UpdateTrailingStop(uint256 posId, uint256 stpPrice);

    modifier onlyVault() {
        require(msg.sender == address(vault), "Only vault");
        _;
    }

    modifier onlyPositionVault() {
        require(msg.sender == address(positionVault), "Only position vault");
        _;
    }

    modifier onlyOperator(uint256 level) {
        require(operators.getOperatorLevel(msg.sender) >= level, "invalid operator");
        _;
    }

    /* ========== INITIALIZE FUNCTIONS ========== */

    function initialize() public initializer {
        __ReentrancyGuard_init();
    }

    function init(
        IPriceManager _priceManager,
        IPositionVault _positionVault,
        ISettingsManager _settingsManager,
        IVault _vault,
        IOperators _operators
    ) external {
        require(!isInitialized, "initialized");
        require(AddressUpgradeable.isContract(address(_priceManager)), "priceManager invalid");
        require(AddressUpgradeable.isContract(address(_positionVault)), "positionVault invalid");
        require(AddressUpgradeable.isContract(address(_settingsManager)), "settingsManager invalid");
        require(AddressUpgradeable.isContract(address(_vault)), "vault invalid");
        require(AddressUpgradeable.isContract(address(_operators)), "operators is invalid");

        priceManager = _priceManager;
        settingsManager = _settingsManager;
        positionVault = _positionVault;
        vault = _vault;
        operators = _operators;

        isInitialized = true;
    }

    /* ========== FOR OPENING POSITIONS ========== */

    function createNewOrder(
        uint256 _posId,
        address _account,
        bool _isLong,
        uint256 _tokenId,
        uint256 _positionType,
        uint256[] memory _params,
        address _refer
    ) external override onlyPositionVault {
        Order storage order = orders[_posId];
        order.status = OrderStatus.PENDING;
        order.positionType = _positionType;
        order.collateral = _params[2];
        order.size = _params[3];
        order.lmtPrice = _params[0];
        order.stpPrice = _params[1];
        order.timestamp = block.timestamp;
        emit NewOrder(_posId, _account, _isLong, _tokenId, order.positionType, order.status, _params, _refer);
    }

    function cancelMarketOrder(uint256 _posId) public override onlyPositionVault {
        // only cancel if the order still exists
        if (orders[_posId].size > 0) {
            Order storage order = orders[_posId];
            order.status = OrderStatus.CANCELED;

            Position memory position = positionVault.getPosition(_posId);
            vault.takeVUSDOut(position.owner, order.collateral + positionVault.getPaidFees(_posId).paidPositionFee);

            emit FinishOrder(_posId, order.positionType, order.status);
        }
    }

    function cancelPendingOrder(address _account, uint256 _posId) external override onlyVault {
        Order storage order = orders[_posId];
        Position memory position = positionVault.getPosition(_posId);
        require(_account == position.owner, "You are not allowed to cancel");
        require(order.status == OrderStatus.PENDING, "Not in Pending");
        require(order.positionType != POSITION_MARKET, "market order cannot be cancelled");
        if (order.positionType == POSITION_TRAILING_STOP) {
            order.status = OrderStatus.FILLED;
            order.positionType = POSITION_MARKET;
        } else {
            order.status = OrderStatus.CANCELED;
            vault.takeVUSDOut(position.owner, order.collateral + positionVault.getPaidFees(_posId).paidPositionFee);
        }
        order.collateral = 0;
        order.size = 0;
        order.lmtPrice = 0;
        order.stpPrice = 0;
        emit FinishOrder(_posId, order.positionType, order.status);
    }

    function updateOrder(
        uint256 _posId,
        uint256 _positionType,
        uint256 _collateral,
        uint256 _size,
        OrderStatus _status
    ) public override onlyPositionVault {
        _updateOrder(_posId, _positionType, _collateral, _size, _status);
    }

    function _updateOrder(
        uint256 _posId,
        uint256 _positionType,
        uint256 _collateral,
        uint256 _size,
        OrderStatus _status
    ) private {
        Order storage order = orders[_posId];
        order.positionType = _positionType;
        order.collateral = _collateral;
        order.size = _size;
        order.status = _status;
        if (_status == OrderStatus.FILLED || _status == OrderStatus.CANCELED) {
            emit FinishOrder(_posId, _positionType, _status);
        } else {
            emit UpdateOrder(_posId, _positionType, _status);
        }
    }

    /* ========== FOR ADDING POSITIONS ========== */

    function createAddPositionOrder(
        address _owner,
        uint256 _posId,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        uint256 _allowedPrice,
        uint256 _fee
    ) external override onlyPositionVault {
        require(addPositionOrders[_posId].size == 0, "addPositionOrder already exists");

        addPositionOrders[_posId] = AddPositionOrder({
            owner: _owner,
            collateral: _collateralDelta,
            size: _sizeDelta,
            allowedPrice: _allowedPrice,
            timestamp: block.timestamp,
            fee: _fee
        });
    }

    function cancelAddPositionOrder(uint256 _posId) external override onlyPositionVault {
        AddPositionOrder memory addPositionOrder = addPositionOrders[_posId];

        if (addPositionOrder.size > 0) {
            vault.takeVUSDOut(addPositionOrder.owner, addPositionOrder.collateral + addPositionOrder.fee);
            delete addPositionOrders[_posId];
        }
    }

    function deleteAddPositionOrder(uint256 _posId) external override onlyPositionVault {
        delete addPositionOrders[_posId];
    }

    /* ========== FOR CLOSING POSITIONS (MARKET ORDER) ========== */

    function createDecreasePositionOrder(
        uint256 _posId,
        uint256 _sizeDelta,
        uint256 _allowedPrice
    ) external override onlyPositionVault {
        require(decreasePositionOrders[_posId].size == 0, "decreasePositionOrder already exists");

        decreasePositionOrders[_posId] = DecreasePositionOrder({
            size: _sizeDelta,
            allowedPrice: _allowedPrice,
            timestamp: block.timestamp
        });
    }

    function deleteDecreasePositionOrder(uint256 _posId) external override onlyPositionVault {
        delete decreasePositionOrders[_posId];
    }

    /* ========== FOR CLOSING POSITIONS (TPSL ORDER) ========== */

    function addTriggerOrders(
        uint256 _posId,
        address _account,
        bool[] memory _isTPs,
        uint256[] memory _prices,
        uint256[] memory _amountPercents
    ) external override onlyVault {
        Position memory position = positionVault.getPosition(_posId);
        require(position.owner == _account, "not allowed");
        require(_prices.length == _isTPs.length && _prices.length == _amountPercents.length, "invalid params");
        require(_prices.length > 0, "empty order");
        require(
            EnumerableSetUpgradeable.length(aliveTriggerIds[_posId]) + _prices.length <=
                settingsManager.maxTriggerPerPosition(),
            "too many triggers"
        );
        PositionTrigger storage triggerOrder = triggerOrders[_posId];

        for (uint256 i; i < _prices.length; ++i) {
            require(_amountPercents[i] > 0 && _amountPercents[i] <= BASIS_POINTS_DIVISOR, "invalid percent");

            uint256 triggersLength = triggerOrder.triggers.length;
            EnumerableSetUpgradeable.add(aliveTriggerIds[_posId], triggersLength);
            triggerOrder.triggers.push(
                TriggerInfo({
                    isTP: _isTPs[i],
                    amountPercent: _amountPercents[i],
                    createdAt: block.timestamp,
                    price: _prices[i],
                    triggeredAmount: 0,
                    triggeredAt: 0,
                    status: TriggerStatus.OPEN
                })
            );
            emit AddTriggerOrders(
                _posId,
                triggersLength,
                _isTPs[i],
                _prices[i],
                _amountPercents[i],
                TriggerStatus.OPEN
            );
        }
    }

    function cancelTriggerOrder(uint256 _posId, uint256 _orderId) external nonReentrant {
        _cancelTriggerOrder(_posId, _orderId);
    }

    function cancelTriggerOrderPacked(uint256 x) external nonReentrant {
        uint256 posId = x / 2 ** 128;
        uint256 orderId = x % 2 ** 128;
        _cancelTriggerOrder(posId, orderId);
    }

    function _cancelTriggerOrder(uint256 _posId, uint256 _orderId) private {
        PositionTrigger storage order = triggerOrders[_posId];
        Position memory position = positionVault.getPosition(_posId);
        require(position.owner == msg.sender, "not allowed");
        require(order.triggers[_orderId].status == TriggerStatus.OPEN, "TriggerOrder was cancelled");
        order.triggers[_orderId].status = TriggerStatus.CANCELLED;
        EnumerableSetUpgradeable.remove(aliveTriggerIds[_posId], _orderId);
        emit UpdateTriggerOrderStatus(_posId, _orderId, order.triggers[_orderId].status);
    }

    function cancelAllTriggerOrders(uint256 _posId) external nonReentrant {
        PositionTrigger storage order = triggerOrders[_posId];
        Position memory position = positionVault.getPosition(_posId);
        require(position.owner == msg.sender, "not allowed");
        uint256 length = EnumerableSetUpgradeable.length(aliveTriggerIds[_posId]);
        require(length > 0, "already cancelled");
        uint256[] memory tmp = new uint256[](length);
        for (uint256 i = 0; i < length; ++i) {
            uint256 idx = EnumerableSetUpgradeable.at(aliveTriggerIds[_posId], i);
            TriggerInfo storage trigger = order.triggers[idx];
            trigger.status = TriggerStatus.CANCELLED;
            emit UpdateTriggerOrderStatus(_posId, idx, trigger.status);
            tmp[i] = idx;
        }
        for (uint256 i = 0; i < length; ++i) {
            EnumerableSetUpgradeable.remove(aliveTriggerIds[_posId], tmp[i]);
        }
    }

    function editTriggerOrder(
        uint256 _posId,
        uint256 _orderId,
        bool _isTP,
        uint256 _price,
        uint256 _amountPercent
    ) external nonReentrant {
        PositionTrigger storage order = triggerOrders[_posId];
        Position memory position = positionVault.getPosition(_posId);
        require(position.owner == msg.sender, "not allowed");
        require(order.triggers[_orderId].status == TriggerStatus.OPEN, "TriggerOrder not Open");
        require(_amountPercent > 0 && _amountPercent <= BASIS_POINTS_DIVISOR, "invalid percent");

        order.triggers[_orderId].isTP = _isTP;
        order.triggers[_orderId].price = _price;
        order.triggers[_orderId].amountPercent = _amountPercent;
        order.triggers[_orderId].createdAt = block.timestamp;

        emit EditTriggerOrder(_posId, _orderId, _isTP, _price, _amountPercent);
    }

    function executeTriggerOrders(uint256 _posId) internal returns (uint256, uint256) {
        PositionTrigger storage order = triggerOrders[_posId];
        Position memory position = positionVault.getPosition(_posId);
        require(position.size > 0, "Trigger Not Open");
        uint256 price = priceManager.getLastPrice(position.tokenId);
        for (uint256 i = 0; i < EnumerableSetUpgradeable.length(aliveTriggerIds[_posId]); ++i) {
            uint256 idx = EnumerableSetUpgradeable.at(aliveTriggerIds[_posId], i);
            TriggerInfo storage trigger = order.triggers[idx];
            if (validateTrigger(trigger.status, trigger.isTP, position.isLong, trigger.price, price)) {
                uint256 triggerAmount = (position.size * trigger.amountPercent) / BASIS_POINTS_DIVISOR;
                trigger.triggeredAmount = triggerAmount;
                trigger.triggeredAt = block.timestamp;
                trigger.status = TriggerStatus.TRIGGERED;
                EnumerableSetUpgradeable.remove(aliveTriggerIds[_posId], idx);
                emit ExecuteTriggerOrders(_posId, trigger.triggeredAmount, idx, price);
                return (triggerAmount, price);
            }
        }
        revert("trigger not ready");
    }

    function validateTrigger(
        TriggerStatus _status,
        bool _isTP,
        bool _isLong,
        uint256 _triggerPrice,
        uint256 _lastPrice
    ) private pure returns (bool) {
        if (_status != TriggerStatus.OPEN) return false;

        if (_isTP) {
            if (_isLong) {
                if (_lastPrice >= _triggerPrice) return true;
            } else {
                if (_lastPrice <= _triggerPrice) return true;
            }
        } else {
            if (_isLong) {
                if (_lastPrice <= _triggerPrice) return true;
            } else {
                if (_lastPrice >= _triggerPrice) return true;
            }
        }

        return false;
    }

    /* ========== FOR CLOSING POSITIONS (TRAILING STOP ORDER) ========== */

    function addTrailingStop(address _account, uint256 _posId, uint256[] memory _params) external override onlyVault {
        Order storage order = orders[_posId];
        Position memory position = positionVault.getPosition(_posId);
        require(_account == position.owner, "you are not allowed to add trailing stop");
        require(position.size > 0, "position not alive");
        validateTrailingStopInputData(_params);
        if (position.size < _params[1]) {
            order.size = position.size;
        } else {
            order.size = _params[1];
        }
        order.collateral = _params[0];
        order.status = OrderStatus.PENDING;
        order.positionType = POSITION_TRAILING_STOP;
        order.stepType = _params[2];
        order.stpPrice = _params[3];
        order.stepAmount = _params[4];
        emit AddTrailingStop(_posId, _params);
    }

    function validateTrailingStopInputData(uint256[] memory _params) public pure returns (bool) {
        require(_params[1] > 0, "trailing size is zero");
        require(_params[4] > 0 && _params[3] > 0, "invalid trailing data");
        require(_params[2] <= 1, "invalid type");
        if (_params[2] == TRAILING_STOP_TYPE_PERCENT) {
            require(_params[4] < BASIS_POINTS_DIVISOR, "percent cant exceed 100%");
        }
        return true;
    }

    function updateTrailingStop(uint256 _posId) external nonReentrant {
        Position memory position = positionVault.getPosition(_posId);
        Order storage order = orders[_posId];
        uint256 price = priceManager.getLastPrice(position.tokenId);
        require(position.owner == msg.sender || operators.getOperatorLevel(msg.sender) >= 1, "updateTStop not allowed");
        require(position.size > 0, "position not alive");
        validateTrailingStopPrice(position.tokenId, position.isLong, _posId, true);
        uint256 oldStpPrice = order.stpPrice;
        if (position.isLong) {
            order.stpPrice = order.stepType == 0
                ? price - order.stepAmount
                : (price * (BASIS_POINTS_DIVISOR - order.stepAmount)) / BASIS_POINTS_DIVISOR;
        } else {
            order.stpPrice = order.stepType == 0
                ? price + order.stepAmount
                : (price * (BASIS_POINTS_DIVISOR + order.stepAmount)) / BASIS_POINTS_DIVISOR;
        }
        uint256 diff;
        if (order.stpPrice > oldStpPrice) {
            diff = order.stpPrice - oldStpPrice;
        } else {
            diff = oldStpPrice - order.stpPrice;
        }
        require(
            (diff * BASIS_POINTS_DIVISOR) / oldStpPrice >= settingsManager.priceMovementPercent(),
            "!price movement"
        );
        emit UpdateTrailingStop(_posId, order.stpPrice);
    }

    function validateTrailingStopPrice(
        uint256 _tokenId,
        bool _isLong,
        uint256 _posId,
        bool _raise
    ) public view returns (bool) {
        Order memory order = orders[_posId];
        uint256 price = priceManager.getLastPrice(_tokenId);
        uint256 stopPrice;
        if (_isLong) {
            if (order.stepType == TRAILING_STOP_TYPE_AMOUNT) {
                stopPrice = order.stpPrice + order.stepAmount;
            } else {
                stopPrice = (order.stpPrice * BASIS_POINTS_DIVISOR) / (BASIS_POINTS_DIVISOR - order.stepAmount);
            }
        } else {
            if (order.stepType == TRAILING_STOP_TYPE_AMOUNT) {
                stopPrice = order.stpPrice - order.stepAmount;
            } else {
                stopPrice = (order.stpPrice * BASIS_POINTS_DIVISOR) / (BASIS_POINTS_DIVISOR + order.stepAmount);
            }
        }
        bool flag;
        if (
            _isLong &&
            order.status == OrderStatus.PENDING &&
            order.positionType == POSITION_TRAILING_STOP &&
            stopPrice <= price
        ) {
            flag = true;
        } else if (
            !_isLong &&
            order.status == OrderStatus.PENDING &&
            order.positionType == POSITION_TRAILING_STOP &&
            stopPrice >= price
        ) {
            flag = true;
        }
        if (_raise) {
            require(flag, "price incorrect");
        }
        return flag;
    }

    /* ========== EXECUTE ORDERS ========== */

    function triggerForOpenOrders(uint256 _posId) external nonReentrant onlyOperator(1) {
        Position memory position = positionVault.getPosition(_posId);
        Order memory order = orders[_posId];
        require(order.status == OrderStatus.PENDING, "order not pending");
        uint256 price = priceManager.getLastPrice(position.tokenId);

        if (order.positionType == POSITION_LIMIT) {
            if (position.isLong) {
                require(order.lmtPrice >= price, "trigger not met");
            } else {
                require(order.lmtPrice <= price, "trigger not met");
            }
            positionVault.increasePosition(
                _posId,
                position.owner,
                position.tokenId,
                position.isLong,
                price,
                order.collateral,
                order.size,
                positionVault.getPaidFees(_posId).paidPositionFee
            );
            _updateOrder(_posId, order.positionType, 0, 0, OrderStatus.FILLED);
            positionVault.removeUserOpenOrder(position.owner, _posId);
        } else if (order.positionType == POSITION_STOP_MARKET) {
            if (position.isLong) {
                require(order.stpPrice <= price, "trigger not met");
            } else {
                require(order.stpPrice >= price, "trigger not met");
            }
            positionVault.increasePosition(
                _posId,
                position.owner,
                position.tokenId,
                position.isLong,
                price,
                order.collateral,
                order.size,
                positionVault.getPaidFees(_posId).paidPositionFee
            );
            _updateOrder(_posId, order.positionType, 0, 0, OrderStatus.FILLED);
            positionVault.removeUserOpenOrder(position.owner, _posId);
        } else if (order.positionType == POSITION_STOP_LIMIT) {
            if (position.isLong) {
                require(order.stpPrice <= price, "trigger not met");
            } else {
                require(order.stpPrice >= price, "trigger not met");
            }
            _updateOrder(_posId, POSITION_LIMIT, order.collateral, order.size, order.status);
        } else if (order.positionType == POSITION_TRAILING_STOP) {
            if (position.isLong) {
                require(order.stpPrice >= price, "trigger not met");
            } else {
                require(order.stpPrice <= price, "trigger not met");
            }
            positionVault.decreasePositionByOrderVault(_posId, price, order.size);
            _updateOrder(_posId, POSITION_MARKET, 0, 0, OrderStatus.FILLED);
        } else {
            revert("!positionType");
        }
    }

    function triggerForTPSL(uint256 _posId) external nonReentrant onlyOperator(1) {
        (uint256 triggeredAmount, uint256 triggerPrice) = executeTriggerOrders(_posId);
        positionVault.decreasePositionByOrderVault(_posId, triggerPrice, triggeredAmount);
    }

    /* ========== VIEW FUNCTIONS ========== */

    function getOrder(uint256 _posId) external view override returns (Order memory) {
        return orders[_posId];
    }

    function getAddPositionOrder(uint256 _posId) external view override returns (AddPositionOrder memory) {
        return addPositionOrders[_posId];
    }

    function getDecreasePositionOrder(uint256 _posId) external view override returns (DecreasePositionOrder memory) {
        return decreasePositionOrders[_posId];
    }

    function getTriggerOrderInfo(uint256 _posId) external view override returns (PositionTrigger memory) {
        return triggerOrders[_posId];
    }

    function getAliveTriggerIds(uint256 _posId) external view returns (uint256[] memory _aliveTriggerIds) {
        uint256 length = EnumerableSetUpgradeable.length(aliveTriggerIds[_posId]);
        _aliveTriggerIds = new uint256[](length);

        for (uint256 i; i < length; ++i) {
            _aliveTriggerIds[i] = EnumerableSetUpgradeable.at(aliveTriggerIds[_posId], i);
        }
    }
}

contracts/core/PositionVault.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./interfaces/ILiquidateVault.sol";
import "./interfaces/IPositionVault.sol";
import "./interfaces/IPriceManager.sol";
import "./interfaces/ISettingsManager.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IOrderVault.sol";
import "./interfaces/IOperators.sol";

import {Constants} from "../access/Constants.sol";
import {OrderStatus, PaidFees, Temp} from "./structs.sol";

contract PositionVault is Constants, Initializable, ReentrancyGuardUpgradeable, IPositionVault {
    // constants
    ISettingsManager private settingsManager;
    ILiquidateVault private liquidateVault;
    IPriceManager private priceManager;
    IOrderVault private orderVault;
    IOperators private operators;
    IVault private vault;
    bool private isInitialized;

    // variables
    uint256 public override lastPosId;
    mapping(uint256 => Position) private positions; // posId => Position{}
    mapping(address => uint256[]) private userPositionIds; // userAddress => alive posIds[]
    mapping(address => uint256[]) private userOpenOrderIds; // userAddress => open orderIds[]
    mapping(uint256 => uint256) private userAliveIndexOf; // posId => index of userPositionIds[user], note that a position can only have a user
    mapping(uint256 => uint256) private userOpenOrderIndexOf; // posId => index of userPositionIds[user], note that a position can only have a user
    mapping(uint256 => PaidFees) private paidFees; // to track paid fees for each position

    // variables to faciliate market order execution (easier to batch execute and track without using event)
    uint256 public queueIndex;
    uint256[] public queuePosIds;

    mapping(uint256 => uint256) public removeCollateralOrders; // posId => collateralAmount

    event AddOrRemoveCollateral(uint256 posId, bool isPlus, uint256 amount, uint256 collateral, uint256 size);
    event ExecuteRemoveCollateral(uint256 posId);
    event ExecuteRemoveCollateralError(uint256 indexed posId, address indexed account, string err);
    event CreateAddPositionOrder(uint256 posId, uint256 collateral, uint256 size, uint256 allowedPrice);
    event CreateDecreasePositionOrder(uint256 posId, uint256 size, uint256 allowedPrice);
    event ExecuteAddPositionOrder(uint256 posId, uint256 collateral, uint256 size, uint256 feeUsd);
    event ExecuteDecreasePositionOrder(uint256 posId, uint256 size);
    event MarketOrderExecutionError(uint256 indexed posId, address indexed account, string err);
    event AddPositionExecutionError(uint256 indexed posId, address indexed account, string err);
    event DecreasePositionExecutionError(uint256 indexed posId, address indexed account, string err);
    event IncreasePosition(
        uint256 indexed posId,
        address indexed account,
        uint256 indexed tokenId,
        bool isLong,
        uint256[5] posData
    );
    event DecreasePosition(
        uint256 indexed posId,
        address indexed account,
        uint256 indexed tokenId,
        bool isLong,
        int256[3] pnlData,
        uint256[5] posData
    );
    event ClosePosition(
        uint256 indexed posId,
        address indexed account,
        uint256 indexed tokenId,
        bool isLong,
        int256[3] pnlData,
        uint256[5] posData
    );

    modifier onlyVault() {
        _onlyVault();
        _;
    }

    function _onlyVault() private view {
        require(msg.sender == address(vault), "Only vault");
    }

    modifier onlyOrderVault() {
        _onlyOrderVault();
        _;
    }

    function _onlyOrderVault() private view {
        require(msg.sender == address(orderVault), "Only vault");
    }

    modifier onlyLiquidateVault() {
        require(msg.sender == address(liquidateVault), "Only vault");
        _;
    }

    modifier onlyOperator(uint256 level) {
        _onlyOperator(level);
        _;
    }

    function _onlyOperator(uint256 level) private view {
        require(operators.getOperatorLevel(msg.sender) >= level, "invalid operator");
    }

    /* ========== INITIALIZE FUNCTIONS ========== */

    function initialize(address _vault, address _priceManager, address _operators) public initializer {
        __ReentrancyGuard_init();
        // intitialize the admins
        vault = IVault(_vault);
        priceManager = IPriceManager(_priceManager);
        operators = IOperators(_operators);
    }

    function init(
        IOrderVault _orderVault,
        ILiquidateVault _liquidateVault,
        ISettingsManager _settingsManager
    ) external {
        require(!isInitialized, "initialized");

        liquidateVault = _liquidateVault;
        orderVault = _orderVault;
        settingsManager = _settingsManager;

        isInitialized = true;
    }

    /* ========== USER FUNCTIONS ========== */

    function newPositionOrder(
        address _account,
        uint256 _tokenId,
        bool _isLong,
        OrderType _orderType,
        // 0 -> market order
        // 1 -> limit order
        // 2 -> stop-market order
        // 3 -> stop-limit order
        uint256[] memory _params,
        // for market order:  _params[0] -> allowed price (revert if exceeded)
        // for limit order: _params[0] -> limit price
        // In stop-market order: _params[1] -> stop price,
        // In stop-limit order: _params[0] -> limit price, _params[1] -> stop price
        // for all orders: _params[2] -> collateral
        // for all orders: _params[3] -> size
        address _refer
    ) external onlyVault {
        validateIncreasePosition(_tokenId, _params[2], _params[3]);

        uint256 _lastPosId = lastPosId;
        Position storage position = positions[_lastPosId];
        position.owner = _account;
        position.refer = _refer;
        position.tokenId = _tokenId;
        position.isLong = _isLong;

        uint256 fee = settingsManager.getTradingFee(_account, _tokenId, _isLong, _params[3]);
        paidFees[_lastPosId].paidPositionFee = fee;
        vault.takeVUSDIn(_account, _params[2] + fee);

        if (_orderType == OrderType.MARKET) {
            require(
                !settingsManager.isIncreasingPositionDisabled(_tokenId),
                "current asset is disabled from increasing position"
            );
            require(_params[0] > 0, "market price is invalid");
            orderVault.createNewOrder(_lastPosId, _account, _isLong, _tokenId, POSITION_MARKET, _params, _refer);
            queuePosIds.push(_lastPosId);
        } else if (_orderType == OrderType.LIMIT) {
            require(_params[0] > 0, "limit price is invalid");
            orderVault.createNewOrder(_lastPosId, _account, _isLong, _tokenId, POSITION_LIMIT, _params, _refer);
            _addUserOpenOrder(position.owner, _lastPosId);
        } else if (_orderType == OrderType.STOP) {
            require(_params[1] > 0, "stop price is invalid");
            orderVault.createNewOrder(_lastPosId, _account, _isLong, _tokenId, POSITION_STOP_MARKET, _params, _refer);
            _addUserOpenOrder(position.owner, _lastPosId);
        } else if (_orderType == OrderType.STOP_LIMIT) {
            require(_params[0] > 0 && _params[1] > 0, "stop limit price is invalid");
            orderVault.createNewOrder(_lastPosId, _account, _isLong, _tokenId, POSITION_STOP_LIMIT, _params, _refer);
            _addUserOpenOrder(position.owner, _lastPosId);
        } else {
            revert("invalid order type");
        }

        lastPosId = _lastPosId + 1;
    }

    function addOrRemoveCollateral(
        address _account,
        uint256 _posId,
        bool isPlus,
        uint256 _amount
    ) external override onlyVault {
        Position storage position = positions[_posId];
        require(_account == position.owner, "you are not allowed to add position");
        require(position.size > 0, "Position not Open");

        if (isPlus) {
            position.collateral += _amount;
            validateMinLeverage(position.size, position.collateral);
            vault.takeVUSDIn(_account, _amount);
            emit AddOrRemoveCollateral(_posId, isPlus, _amount, position.collateral, position.size);
        } else {
            require(removeCollateralOrders[_posId] == 0, "order already exists");
            removeCollateralOrders[_posId] = _amount;

            queuePosIds.push(3 * 2 ** 128 + _posId);
        }
    }

    function createAddPositionOrder(
        address _account,
        uint256 _posId,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        uint256 _allowedPrice
    ) external override onlyVault {
        Position memory position = positions[_posId];

        require(
            !settingsManager.isIncreasingPositionDisabled(position.tokenId),
            "current asset is disabled from increasing position"
        );
        require(position.size > 0, "Position not Open");
        require(_account == position.owner, "you are not allowed to add position");
        validateIncreasePosition(position.tokenId, _collateralDelta, _sizeDelta);

        uint256 fee = settingsManager.getTradingFee(_account, position.tokenId, position.isLong, _sizeDelta);
        vault.takeVUSDIn(_account, _collateralDelta + fee);
        orderVault.createAddPositionOrder(_account, _posId, _collateralDelta, _sizeDelta, _allowedPrice, fee);

        queuePosIds.push(2 ** 128 + _posId);

        emit CreateAddPositionOrder(_posId, _collateralDelta, _sizeDelta, _allowedPrice);
    }

    function createDecreasePositionOrder(
        uint256 _posId,
        address _account,
        uint256 _sizeDelta,
        uint256 _allowedPrice
    ) external override onlyVault {
        Position memory position = positions[_posId];

        require(_sizeDelta > 0, "invalid size");
        require(position.size > 0, "Position not Open");
        require(_account == position.owner, "not allowed");

        orderVault.createDecreasePositionOrder(_posId, _sizeDelta, _allowedPrice);

        queuePosIds.push(2 ** 129 + _posId);

        emit CreateDecreasePositionOrder(_posId, _sizeDelta, _allowedPrice);
    }

    // allow users to close their positions themselves after selfExecuteCooldown, in case keepers are down
    function selfExecuteDecreasePositionOrder(uint256 _posId) external nonReentrant {
        Position memory position = positions[_posId];
        require(msg.sender == position.owner, "!owner");

        DecreasePositionOrder memory decreasePositionOrder = orderVault.getDecreasePositionOrder(_posId);
        require(
            block.timestamp > decreasePositionOrder.timestamp + settingsManager.selfExecuteCooldown(),
            "cannot self execute yet"
        );

        uint256 price = priceManager.getLastPrice(position.tokenId);
        // without check slippage to ensure success execute
        // user should read contract price feed to know the price before execution
        _decreasePosition(_posId, price, decreasePositionOrder.size);
        orderVault.deleteDecreasePositionOrder(_posId);
    }

    /* ========== OPERATOR FUNCTIONS ========== */

    function executeRemoveCollateral(uint256 _posId) external nonReentrant onlyOperator(1) {
        uint256 removeCollateralAmount = removeCollateralOrders[_posId];
        require(removeCollateralAmount > 0, "empty order");
        Position storage position = positions[_posId];
        require(position.size > 0, "Position not Open");

        position.collateral -= removeCollateralAmount;
        validateMaxLeverage(position.tokenId, position.size, position.collateral);
        (bool isPositionLiquidatable, , , ) = liquidateVault.validateLiquidationWithPosid(_posId);
        require(!isPositionLiquidatable, "position will be liquidated");
        vault.takeVUSDOut(position.owner, removeCollateralAmount);

        delete removeCollateralOrders[_posId];

        emit AddOrRemoveCollateral(_posId, false, removeCollateralAmount, position.collateral, position.size);
    }

    function executeOpenMarketOrder(uint256 _posId) public nonReentrant onlyOperator(1) {
        Position memory position = positions[_posId];
        Order memory order = orderVault.getOrder(_posId);

        require(order.size > 0 && order.status == OrderStatus.PENDING, "not open");
        require(order.positionType == POSITION_MARKET, "not market order");
        require(block.timestamp <= order.timestamp + settingsManager.expiryDuration(), "order has expired");

        uint256 price = priceManager.getLastPrice(position.tokenId);
        uint256 priceWithSlippage = settingsManager.getPriceWithSlippage(
            position.tokenId,
            position.isLong,
            order.size,
            price
        );
        checkSlippage(position.isLong, order.lmtPrice, priceWithSlippage);

        _increasePosition(
            _posId,
            position.owner,
            position.tokenId,
            position.isLong,
            price,
            order.collateral,
            order.size,
            paidFees[_posId].paidPositionFee
        );
        orderVault.updateOrder(_posId, order.positionType, 0, 0, OrderStatus.FILLED);
    }

    function executeAddPositionOrder(uint256 _posId) external nonReentrant onlyOperator(1) {
        Position memory position = positions[_posId];
        AddPositionOrder memory addPositionOrder = orderVault.getAddPositionOrder(_posId);

        require(addPositionOrder.size > 0, "order size is 0");
        require(block.timestamp <= addPositionOrder.timestamp + settingsManager.expiryDuration(), "order has expired");

        uint256 price = priceManager.getLastPrice(position.tokenId);
        uint256 priceWithSlippage = settingsManager.getPriceWithSlippage(
            position.tokenId,
            position.isLong,
            addPositionOrder.size,
            price
        );
        checkSlippage(position.isLong, addPositionOrder.allowedPrice, priceWithSlippage);

        _increasePosition(
            _posId,
            position.owner,
            position.tokenId,
            position.isLong,
            price,
            addPositionOrder.collateral,
            addPositionOrder.size,
            addPositionOrder.fee
        );
        orderVault.deleteAddPositionOrder(_posId);

        emit ExecuteAddPositionOrder(_posId, addPositionOrder.collateral, addPositionOrder.size, addPositionOrder.fee);
    }

    function executeDecreasePositionOrder(uint256 _posId) external nonReentrant onlyOperator(1) {
        Position memory position = positions[_posId];
        DecreasePositionOrder memory decreasePositionOrder = orderVault.getDecreasePositionOrder(_posId);

        require(
            block.timestamp <= decreasePositionOrder.timestamp + settingsManager.expiryDuration(),
            "order has expired"
        );

        uint256 decreaseSize = decreasePositionOrder.size > position.size ? position.size : decreasePositionOrder.size;
        uint256 price = priceManager.getLastPrice(position.tokenId);
        uint256 priceWithSlippage = settingsManager.getPriceWithSlippage(
            position.tokenId,
            !position.isLong, // decreasePosition is in opposite direction
            decreaseSize,
            price
        );
        checkSlippage(!position.isLong, decreasePositionOrder.allowedPrice, priceWithSlippage);

        _decreasePosition(_posId, price, decreaseSize);
        orderVault.deleteDecreasePositionOrder(_posId);

        emit ExecuteDecreasePositionOrder(_posId, decreaseSize);
    }

    function executeOrders(uint256 numOfOrders) external onlyOperator(1) {
        uint256 index = queueIndex;
        uint256 endIndex = index + numOfOrders;
        uint256 length = queuePosIds.length;

        if (index >= length) revert("nothing to execute");
        if (endIndex > length) endIndex = length;

        while (index < endIndex) {
            uint256 t = queuePosIds[index];
            uint256 orderType = t / 2 ** 128;
            uint256 posId = t % 2 ** 128;

            if (orderType == 0) {
                try this.executeOpenMarketOrder(posId) {} catch Error(string memory err) {
                    orderVault.cancelMarketOrder(posId);
                    emit MarketOrderExecutionError(posId, positions[posId].owner, err);
                } catch {
                    orderVault.cancelMarketOrder(posId);
                }
            } else if (orderType == 1) {
                try this.executeAddPositionOrder(posId) {} catch Error(string memory err) {
                    orderVault.cancelAddPositionOrder(posId);
                    emit AddPositionExecutionError(posId, positions[posId].owner, err);
                } catch {
                    orderVault.cancelAddPositionOrder(posId);
                }
            } else if (orderType == 2) {
                try this.executeDecreasePositionOrder(posId) {} catch Error(string memory err) {
                    orderVault.deleteDecreasePositionOrder(posId);
                    emit DecreasePositionExecutionError(posId, positions[posId].owner, err);
                } catch {
                    orderVault.deleteDecreasePositionOrder(posId);
                }
            } else if (orderType == 3) {
                try this.executeRemoveCollateral(posId) {} catch Error(string memory err) {
                    delete removeCollateralOrders[posId];
                    emit ExecuteRemoveCollateralError(posId, positions[posId].owner, err);
                } catch {
                    delete removeCollateralOrders[posId];
                }
            }

            delete queuePosIds[index];
            ++index;
        }

        queueIndex = index;
    }

    /* ========== HELPER FUNCTIONS ========== */

    function _increasePosition(
        uint256 _posId,
        address _account,
        uint256 _tokenId,
        bool _isLong,
        uint256 _price,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        uint256 _fee
    ) internal {
        require(
            !settingsManager.isIncreasingPositionDisabled(_tokenId),
            "current asset is disabled from increasing position"
        );

        Position storage position = positions[_posId];

        validateIncreasePosition(position.tokenId, position.collateral + _collateralDelta, position.size + _sizeDelta);

        _price = settingsManager.getPriceWithSlippage(position.tokenId, position.isLong, _sizeDelta, _price);

        settingsManager.updateFunding(_tokenId);
        settingsManager.increaseOpenInterest(_tokenId, _account, _isLong, _sizeDelta);

        if (position.size == 0) {
            position.averagePrice = _price;
            position.fundingIndex = settingsManager.fundingIndex(_tokenId);

            _addUserAlivePosition(_account, _posId);
        } else {
            position.averagePrice =
                (position.size * position.averagePrice + _sizeDelta * _price) /
                (position.size + _sizeDelta);
            position.fundingIndex =
                (int256(position.size) *
                    position.fundingIndex +
                    int256(_sizeDelta) *
                    settingsManager.fundingIndex(_tokenId)) /
                int256(position.size + _sizeDelta);
            position.accruedBorrowFee += settingsManager.getBorrowFee(
                position.size,
                position.lastIncreasedTime,
                _tokenId,
                _isLong
            );

            paidFees[_posId].paidPositionFee += _fee;
        }

        position.collateral += _collateralDelta;
        position.size += _sizeDelta;
        position.lastIncreasedTime = block.timestamp;

        vault.distributeFee(_fee, position.refer, _account);

        emit IncreasePosition(
            _posId,
            _account,
            _tokenId,
            _isLong,
            [_collateralDelta, _sizeDelta, position.averagePrice, _price, _fee]
        );
    }

    function increasePosition(
        uint256 _posId,
        address _account,
        uint256 _tokenId,
        bool _isLong,
        uint256 _price,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        uint256 _fee
    ) external override onlyOrderVault {
        _increasePosition(_posId, _account, _tokenId, _isLong, _price, _collateralDelta, _sizeDelta, _fee);
    }

    function _decreasePosition(uint256 _posId, uint256 _price, uint256 _sizeDelta) internal {
        Position storage position = positions[_posId];

        require(
            !settingsManager.isDecreasingPositionDisabled(position.tokenId),
            "current asset is disabled from decreasing position"
        );
        require(position.size > 0, "position size is zero");
        require(_sizeDelta > 0, "_sizeDelta is zero");
        require(block.timestamp > position.lastIncreasedTime + settingsManager.closeDeltaTime(), "!closeDeltaTime");

        if (_sizeDelta >= position.size) _sizeDelta = position.size;

        _price = settingsManager.getPriceWithSlippage(position.tokenId, !position.isLong, _sizeDelta, _price); // decreasePosition is in opposite direction

        settingsManager.updateFunding(position.tokenId);
        settingsManager.decreaseOpenInterest(position.tokenId, position.owner, position.isLong, _sizeDelta);

        uint256 countedBorrowFee;
        if (position.accruedBorrowFee > 0) {
            countedBorrowFee = (position.accruedBorrowFee * _sizeDelta) / position.size;
            position.accruedBorrowFee -= countedBorrowFee;
        }

        (int256 pnl, int256 fundingFee, int256 borrowFee) = settingsManager.getPnl(
            position.tokenId,
            position.isLong,
            _sizeDelta,
            position.averagePrice,
            _price,
            position.lastIncreasedTime,
            countedBorrowFee,
            position.fundingIndex
        );

        uint256 fee = settingsManager.getTradingFee(position.owner, position.tokenId, !position.isLong, _sizeDelta);

        uint256 collateralDelta = (position.collateral * _sizeDelta) / position.size;

        int256 usdOut = int256(collateralDelta) + pnl - int256(fee);
        if (usdOut > 0) vault.takeVUSDOut(position.owner, uint256(usdOut));

        if (pnl >= 0) {
            if (block.timestamp - position.lastIncreasedTime < settingsManager.minProfitDurations(position.tokenId)) {
                uint256 profitPercent = (BASIS_POINTS_DIVISOR * uint256(pnl)) / collateralDelta;
                if (
                    profitPercent > settingsManager.maxCloseProfitPercents(position.tokenId) ||
                    uint256(pnl) > settingsManager.maxCloseProfits(position.tokenId)
                ) {
                    revert("min profit duration not yet passed");
                }
            }

            vault.accountDeltaIntoTotalUSD(false, uint256(pnl));
        } else {
            uint256 loss = uint256(-1 * pnl);
            uint256 maxLoss = collateralDelta - fee;
            if (loss > maxLoss) {
                vault.accountDeltaIntoTotalUSD(true, maxLoss);
            } else {
                vault.accountDeltaIntoTotalUSD(true, loss);
            }
        }

        vault.distributeFee(fee, position.refer, position.owner);

        // split fundingFee & borrowFee with vault & feeManager
        {
            int256 totalFees = fundingFee + borrowFee;
            if (totalFees >= 0) {
                uint256 totalFeesForFeeManager = (uint256(totalFees) *
                    (BASIS_POINTS_DIVISOR - settingsManager.feeRewardBasisPoints())) / BASIS_POINTS_DIVISOR;
                // take out accounted fees from vault and send to feeManager
                vault.accountDeltaIntoTotalUSD(false, totalFeesForFeeManager);
                vault.takeVUSDOut(settingsManager.feeManager(), totalFeesForFeeManager);
            } else {
                uint256 totalFeesForFeeManager = (uint256(-1 * totalFees) *
                    (BASIS_POINTS_DIVISOR - settingsManager.feeRewardBasisPoints())) / BASIS_POINTS_DIVISOR;
                // take out fees from feeManager and send to vault
                vault.accountDeltaIntoTotalUSD(true, totalFeesForFeeManager);
                vault.takeVUSDIn(settingsManager.feeManager(), totalFeesForFeeManager);
            }
        }

        if (_sizeDelta < position.size) {
            position.size -= _sizeDelta;
            position.collateral -= collateralDelta;
            paidFees[_posId].paidPositionFee += fee;
            paidFees[_posId].paidBorrowFee += uint256(borrowFee);
            paidFees[_posId].paidFundingFee += fundingFee;

            Temp memory temp = Temp({a: collateralDelta, b: _sizeDelta, c: position.averagePrice, d: _price, e: fee}); // use struct to prevent stack too deep error
            emit DecreasePosition(
                _posId,
                position.owner,
                position.tokenId,
                position.isLong,
                [pnl, fundingFee, borrowFee],
                [temp.a, temp.b, temp.c, temp.d, temp.e]
            );
        } else {
            Temp memory temp = Temp({a: collateralDelta, b: _sizeDelta, c: position.averagePrice, d: _price, e: fee}); // use struct to prevent stack too deep error
            emit ClosePosition(
                _posId,
                position.owner,
                position.tokenId,
                position.isLong,
                [pnl, fundingFee, borrowFee],
                [temp.a, temp.b, temp.c, temp.d, temp.e]
            );

            _removeUserAlivePosition(position.owner, _posId);
        }
    }

    // for vault to directly close user's position in forceClosePosition()
    function decreasePosition(uint256 _posId, uint256 _price, uint256 _sizeDelta) external override onlyVault {
        _decreasePosition(_posId, _price, _sizeDelta);
    }

    function decreasePositionByOrderVault(
        uint256 _posId,
        uint256 _price,
        uint256 _sizeDelta
    ) external override onlyOrderVault {
        _decreasePosition(_posId, _price, _sizeDelta);
    }

    function _addUserAlivePosition(address _user, uint256 _posId) internal {
        userAliveIndexOf[_posId] = userPositionIds[_user].length;
        userPositionIds[_user].push(_posId);
    }

    function _addUserOpenOrder(address _user, uint256 _posId) internal {
        userOpenOrderIndexOf[_posId] = userOpenOrderIds[_user].length;
        userOpenOrderIds[_user].push(_posId);
    }

    function removeUserAlivePosition(address _user, uint256 _posId) external override onlyLiquidateVault {
        _removeUserAlivePosition(_user, _posId);
    }

    function _removeUserAlivePosition(address _user, uint256 _posId) internal {
        uint256 index = userAliveIndexOf[_posId];
        uint256 lastIndex = userPositionIds[_user].length - 1;
        uint256 lastId = userPositionIds[_user][lastIndex];
        delete positions[_posId];
        userAliveIndexOf[lastId] = index;
        delete userAliveIndexOf[_posId];

        userPositionIds[_user][index] = lastId;
        userPositionIds[_user].pop();

        orderVault.cancelAddPositionOrder(_posId);
    }

    function removeUserOpenOrder(address _user, uint256 _posId) external override onlyOrderVault {
        _removeUserOpenOrder(_user, _posId);
    }

    function _removeUserOpenOrder(address _user, uint256 _posId) internal {
        uint256 index = userOpenOrderIndexOf[_posId];
        uint256 lastIndex = userOpenOrderIds[_user].length - 1;
        uint256 lastId = userOpenOrderIds[_user][lastIndex];
        userOpenOrderIndexOf[lastId] = index;
        delete userOpenOrderIndexOf[_posId];
        userOpenOrderIds[_user][index] = lastId;
        userOpenOrderIds[_user].pop();
    }

    /* ========== VALIDATE FUNCTIONS ========== */

    function validateIncreasePosition(uint256 _tokenId, uint256 _collateral, uint256 _size) internal view {
        require(_collateral >= settingsManager.minCollateral(), "!minCollateral");
        validateMinLeverage(_size, _collateral);
        validateMaxLeverage(_tokenId, _size, _collateral);
    }

    function validateMinLeverage(uint256 _size, uint256 _collateral) internal pure {
        require(_size >= _collateral, "leverage cannot be less than 1");
    }

    function validateMaxLeverage(uint256 _tokenId, uint256 _size, uint256 _collateral) internal view {
        require(_size * MIN_LEVERAGE <= _collateral * priceManager.maxLeverage(_tokenId), "maxLeverage exceeded");
    }

    /* ========== VIEW FUNCTIONS ========== */

    function getPosition(uint256 _posId) external view override returns (Position memory) {
        return positions[_posId];
    }

    function getUserPositionIds(address _account) external view override returns (uint256[] memory) {
        return userPositionIds[_account];
    }

    function getUserOpenOrderIds(address _account) external view override returns (uint256[] memory) {
        return userOpenOrderIds[_account];
    }

    function getPaidFees(uint256 _posId) external view override returns (PaidFees memory) {
        return paidFees[_posId];
    }

    function getNumOfUnexecuted() external view returns (uint256) {
        return queuePosIds.length - queueIndex;
    }

    function getVaultUSDBalance() external view override returns (uint256) {
        return vault.getVaultUSDBalance();
    }
}

contracts/core/PriceManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./interfaces/IPriceManager.sol";
import "./interfaces/IOperators.sol";
import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";
import {Constants} from "../access/Constants.sol";

contract PriceManager is Constants, Initializable, IPriceManager {
    IOperators public operators;
    IPyth public pyth;

    mapping(uint256 => Asset) public assets;
    struct Asset {
        string symbol;
        bytes32 pythId;
        uint256 price;
        uint256 timestamp;
        uint256 allowedStaleness;
        uint256 allowedDeviation;
        uint256 maxLeverage;
        uint256 tokenDecimals; // for usd stablecoin only
    }

    mapping(address => uint256) public tokenAddressToAssetId; // for usd stablecoin

    // an array to track valid assets
    uint256[] private validAssetIds;

    event SetAsset(
        uint256 assetId,
        string symbol,
        bytes32 pythId,
        uint256 price,
        uint256 timestamp,
        uint256 allowedStaleness,
        uint256 allowedDeviation,
        uint256 maxLeverage
    );
    event SetUsdAsset(
        address tokenAddress,
        uint256 assetId,
        string symbol,
        bytes32 pythId,
        uint256 price,
        uint256 timestamp,
        uint256 allowedStaleness,
        uint256 allowedDeviation,
        uint256 tokenDecimals
    );
    event SetPrice(uint256 assetId, uint256 price, uint256 timestamp);

    modifier onlyOperator(uint256 level) {
        require(operators.getOperatorLevel(msg.sender) >= level, "invalid operator");
        _;
    }

    function initialize(address _operators, address _pyth) public initializer {
        require(AddressUpgradeable.isContract(_operators), "operators invalid");
        require(AddressUpgradeable.isContract(_pyth), "pyth invalid");

        operators = IOperators(_operators);
        pyth = IPyth(_pyth);
    }

    function setAsset(
        uint256 _assetId,
        string calldata _symbol,
        bytes32 _pythId,
        uint256 _price,
        uint256 _allowedStaleness,
        uint256 _allowedDeviation,
        uint256 _maxLeverage
    ) external onlyOperator(3) {
        require(_maxLeverage > MIN_LEVERAGE, "Max Leverage should be greater than Min Leverage");

        // new asset
        if (assets[_assetId].maxLeverage == 0) {
            validAssetIds.push(_assetId);
        }

        assets[_assetId] = Asset({
            symbol: _symbol,
            pythId: _pythId,
            price: _price,
            timestamp: block.timestamp,
            allowedStaleness: _allowedStaleness,
            allowedDeviation: _allowedDeviation,
            maxLeverage: _maxLeverage,
            tokenDecimals: 0
        });

        emit SetAsset(
            _assetId,
            _symbol,
            _pythId,
            _price,
            block.timestamp,
            _allowedStaleness,
            _allowedDeviation,
            _maxLeverage
        );
    }

    function batchSetAllowedDeviation(uint256[] memory _assetIds, uint256 _allowedDeviation) external onlyOperator(3) {
        for(uint256 i; i<_assetIds.length; i++){
            uint256 _assetId = _assetIds[i];
            Asset memory asset = assets[_assetId];
            require(asset.maxLeverage > 0, "!newAsset");
            asset.allowedDeviation = _allowedDeviation;
            assets[_assetId] = asset;
            emit SetAsset(
                _assetId,
                asset.symbol,
                asset.pythId,
                asset.price,
                asset.timestamp,
                asset.allowedStaleness,
                asset.allowedDeviation,
                asset.maxLeverage
            );
        }
    }

    function batchSetAllowedStaleness(uint256[] memory _assetIds, uint256 _allowedStaleness) external onlyOperator(3) {
        for(uint256 i; i<_assetIds.length; i++){
            uint256 _assetId = _assetIds[i];
            Asset memory asset = assets[_assetId];
            require(asset.maxLeverage > 0, "!newAsset");
            asset.allowedStaleness = _allowedStaleness;
            assets[_assetId] = asset;
            emit SetAsset(
                _assetId,
                asset.symbol,
                asset.pythId,
                asset.price,
                asset.timestamp,
                asset.allowedStaleness,
                asset.allowedDeviation,
                asset.maxLeverage
            );
        }
    }

    function batchSetMaxLeverage(uint256[] memory _assetIds, uint256 _maxLeverage) external onlyOperator(3) {
        for(uint256 i; i<_assetIds.length; i++){
            uint256 _assetId = _assetIds[i];
            Asset memory asset = assets[_assetId];
            require(asset.maxLeverage > 0, "!newAsset");
            asset.maxLeverage = _maxLeverage;
            assets[_assetId] = asset;
            emit SetAsset(
                _assetId,
                asset.symbol,
                asset.pythId,
                asset.price,
                asset.timestamp,
                asset.allowedStaleness,
                asset.allowedDeviation,
                asset.maxLeverage
            );
        }
    }

    function setUsdAsset(
        address _tokenAddress,
        uint256 _assetId,
        string calldata _symbol,
        bytes32 _pythId,
        uint256 _price,
        uint256 _allowedStaleness,
        uint256 _allowedDeviation,
        uint256 _tokenDecimals
    ) external onlyOperator(3) {
        // new asset
        if (assets[_assetId].tokenDecimals == 0) {
            validAssetIds.push(_assetId);
        }

        tokenAddressToAssetId[_tokenAddress] = _assetId;
        assets[_assetId] = Asset({
            symbol: _symbol,
            pythId: _pythId,
            price: _price,
            timestamp: block.timestamp,
            allowedStaleness: _allowedStaleness,
            allowedDeviation: _allowedDeviation,
            maxLeverage: 0,
            tokenDecimals: _tokenDecimals
        });

        emit SetUsdAsset(
            _tokenAddress,
            _assetId,
            _symbol,
            _pythId,
            _price,
            block.timestamp,
            _allowedStaleness,
            _allowedDeviation,
            _tokenDecimals
        );
    }

    function getPythLastPrice(uint256 _assetId, bool _requireFreshness) public view returns (uint256) {
        PythStructs.Price memory priceInfo = pyth.getPriceUnsafe(assets[_assetId].pythId);
        if (_requireFreshness) {
            require(block.timestamp <= priceInfo.publishTime + assets[_assetId].allowedStaleness, "price stale");
        }

        uint256 price = uint256(uint64(priceInfo.price));
        if (priceInfo.expo >= 0) {
            uint256 exponent = uint256(uint32(priceInfo.expo));
            return price * PRICE_PRECISION * (10 ** exponent);
        } else {
            uint256 exponent = uint256(uint32(-priceInfo.expo));
            return (price * PRICE_PRECISION) / (10 ** exponent);
        }
    }

    function getLastPrice(uint256 _assetId) public view override returns (uint256) {
        uint256 price = assets[_assetId].price;
        require(price > 0, "invalid price");

        uint256 ts = assets[_assetId].timestamp;
        uint256 allowedStaleness = assets[_assetId].allowedStaleness;
        if (allowedStaleness == 0 || block.timestamp - ts <= allowedStaleness) {
            // our price is fresh enough, return our answer
            return price;
        } else {
            // our price is stale, try use on-chain price with freshness requirement
            return getPythLastPrice(_assetId, true);
        }
    }

    function setPrice(uint256 _assetId, uint256 _price, uint256 _ts) public onlyOperator(2) {
        require(_ts > assets[_assetId].timestamp, "already updated");
        bytes32 pythId = assets[_assetId].pythId;
        if (pythId != bytes32(0)) {
            //skip validation if pyth not enabled for this asset
            uint256 priceOnChain = getPythLastPrice(_assetId, false);
            uint256 deviation = _price > priceOnChain
                ? ((_price - priceOnChain) * BASIS_POINTS_DIVISOR) / priceOnChain
                : ((priceOnChain - _price) * BASIS_POINTS_DIVISOR) / priceOnChain;
            require(deviation <= assets[_assetId].allowedDeviation, "need update pyth price");
        }
        assets[_assetId].price = _price;
        assets[_assetId].timestamp = _ts;

        emit SetPrice(_assetId, _price, _ts);
    }

    function tokenToUsd(address _token, uint256 _tokenAmount) external view override returns (uint256) {
        uint256 assetId = tokenAddressToAssetId[_token];

        return (_tokenAmount * getLastPrice(assetId)) / (10 ** assets[assetId].tokenDecimals);
    }

    function usdToToken(address _token, uint256 _usdAmount) external view override returns (uint256) {
        uint256 assetId = tokenAddressToAssetId[_token];

        return (_usdAmount * (10 ** assets[assetId].tokenDecimals)) / getLastPrice(assetId);
    }

    function getCurrentTime() external view returns (uint256) {
        return block.timestamp;
    }

    function maxLeverage(uint256 _assetId) external view override returns (uint256) {
        return assets[_assetId].maxLeverage;
    }

    function getValidAssetIds() external view returns (uint256[] memory) {
        return validAssetIds;
    }
}

contracts/core/Reader.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IPositionVault.sol";
import "./interfaces/IOrderVault.sol";
import "./interfaces/IVault.sol";
import "./interfaces/ISettingsManager.sol";

import "../staking/interfaces/ITokenFarm.sol";

import {Constants} from "../access/Constants.sol";
import {OrderStatus, PositionTrigger, TriggerInfo, PaidFees} from "./structs.sol";

contract Reader is Constants, Initializable {
    struct AccruedFees {
        uint256 positionFee;
        uint256 borrowFee;
        int256 fundingFee;
    }

    IOrderVault private orderVault;
    IPositionVault private positionVault;
    ISettingsManager private settingsManager;
    ITokenFarm private tokenFarm;
    IVault private vault;
    IERC20 private USDC;
    IERC20 private vUSD;
    IERC20 private vlp;
    IERC20 private vela;
    IERC20 private esVela;

    function initialize(IPositionVault _positionVault, IOrderVault _orderVault, ISettingsManager _settingsManager) public initializer {
        require(AddressUpgradeable.isContract(address(_positionVault)), "positionVault invalid");
        require(AddressUpgradeable.isContract(address(_orderVault)), "orderVault invalid");
        positionVault = _positionVault;
        orderVault = _orderVault;
        settingsManager = _settingsManager;
    }

    function initializeV2(ITokenFarm _tokenFarm) reinitializer(2) public {
        tokenFarm = _tokenFarm; // first caller 2%, resolver 8% and leftover 90% to vlp
        require(AddressUpgradeable.isContract(address(_tokenFarm)), "tokenFarm invalid");
    }

    function initializeV3(IVault _vault, IERC20 _USDC, IERC20 _vUSD, IERC20 _vlp, IERC20 _vela, IERC20 _esVela) reinitializer(3) public {
        require(AddressUpgradeable.isContract(address(_esVela)), "esVela invalid");
        require(AddressUpgradeable.isContract(address(_vault)), "Vault invalid");
        require(AddressUpgradeable.isContract(address(_vela)), "vela invalid");
        require(AddressUpgradeable.isContract(address(_vlp)), "vlp invalid");
        require(AddressUpgradeable.isContract(address(_USDC)), "USDC invalid");
        require(AddressUpgradeable.isContract(address(_vUSD)), "vUSD invalid");
        esVela = _esVela;
        vault = _vault;
        vela = _vela;
        vlp = _vlp;
        USDC = _USDC;
        vUSD = _vUSD;
    }

    function getUserAlivePositions(
        address _user
    )
        public
        view
        returns (uint256[] memory, Position[] memory, Order[] memory, PositionTrigger[] memory, PaidFees[] memory, AccruedFees[] memory)
    {
        uint256[] memory posIds = positionVault.getUserPositionIds(_user);
        uint256 length = posIds.length;
        Position[] memory positions_ = new Position[](length);
        Order[] memory orders_ = new Order[](length);
        PositionTrigger[] memory triggers_ = new PositionTrigger[](length);
        PaidFees[] memory paidFees_ = new PaidFees[](length);
        AccruedFees[] memory accruedFees_ = new AccruedFees[](length);
        for (uint i; i < length; ++i) {
            uint256 posId = posIds[i];
            positions_[i] = positionVault.getPosition(posId);
            orders_[i] = orderVault.getOrder(posId);
            triggers_[i] = orderVault.getTriggerOrderInfo(posId);
            paidFees_[i] = positionVault.getPaidFees(posId);
            accruedFees_[i] = getAccruedFee(posId);
        }
        return (posIds, positions_, orders_, triggers_, paidFees_, accruedFees_);
    }

    function getAccruedFee(uint256 _posId) internal view returns (AccruedFees memory){
        Position memory position = positionVault.getPosition(_posId);
        AccruedFees memory accruedFees;
        accruedFees.positionFee = settingsManager.getTradingFee(position.owner, position.tokenId, position.isLong, position.size);
        accruedFees.borrowFee = settingsManager.getBorrowFee(position.size, position.lastIncreasedTime, position.tokenId, position.isLong) + position.accruedBorrowFee;
        accruedFees.fundingFee = settingsManager.getFundingFee(position.tokenId, position.isLong, position.size, position.fundingIndex);
        return accruedFees;
    }

    function getGlobalInfo(
        address _account,
        uint256 _tokenId
    )
        external
        view
        returns (
            int256 fundingRate,
            uint256 borrowRateForLong,
            uint256 borrowRateForShort,
            uint256 longOpenInterest,
            uint256 shortOpenInterest,
            uint256 maxLongOpenInterest,
            uint256 maxShortOpenInterest,
            uint256 longTradingFee,
            uint256 shortTradingFee
        )
    {
        fundingRate = settingsManager.getFundingRate(_tokenId);
        borrowRateForLong = settingsManager.getBorrowRate(_tokenId, true);
        borrowRateForShort = settingsManager.getBorrowRate(_tokenId, false);
        longOpenInterest = settingsManager.openInterestPerAssetPerSide(_tokenId, true);
        shortOpenInterest = settingsManager.openInterestPerAssetPerSide(_tokenId, false);
        maxLongOpenInterest = settingsManager.maxOpenInterestPerAssetPerSide(_tokenId, true);
        maxShortOpenInterest = settingsManager.maxOpenInterestPerAssetPerSide(_tokenId, false);
        longTradingFee = settingsManager.getTradingFee(_account, _tokenId, true, PRICE_PRECISION);
        shortTradingFee = settingsManager.getTradingFee(_account, _tokenId, false, PRICE_PRECISION);
    }

    function getUserOpenOrders(
        address _user
    )
        public
        view
        returns (uint256[] memory, Position[] memory, Order[] memory, PositionTrigger[] memory, PaidFees[] memory, AccruedFees[] memory)
    {
        uint256[] memory posIds = positionVault.getUserOpenOrderIds(_user);
        uint256 length = posIds.length;
        Position[] memory positions_ = new Position[](length);
        Order[] memory orders_ = new Order[](length);
        PositionTrigger[] memory triggers_ = new PositionTrigger[](length);
        PaidFees[] memory paidFees_ = new PaidFees[](length);
        AccruedFees[] memory accruedFees_ = new AccruedFees[](length);
        for (uint i; i < length; ++i) {
            uint256 posId = posIds[i];
            positions_[i] = positionVault.getPosition(posId);
            orders_[i] = orderVault.getOrder(posId);
            triggers_[i] = orderVault.getTriggerOrderInfo(posId);
            paidFees_[i] = positionVault.getPaidFees(posId);
            accruedFees_[i] = getAccruedFee(posId);
        }
        return (posIds, positions_, orders_, triggers_, paidFees_, accruedFees_);
    }

    function getFeesFor1CT(address _normal, address _oneCT) external view returns (bool, uint256) {
        uint256 tierVelaPercent = tokenFarm.getTierVela(_normal);
        uint256 deductFeePercentForNormal = settingsManager.deductFeePercent(_normal);
        uint256 deductFeePercentForOneCT = settingsManager.deductFeePercent(_oneCT);
        if (tierVelaPercent * (BASIS_POINTS_DIVISOR - deductFeePercentForNormal) / BASIS_POINTS_DIVISOR != (BASIS_POINTS_DIVISOR - deductFeePercentForOneCT)) {
            return (true, BASIS_POINTS_DIVISOR - tierVelaPercent * (BASIS_POINTS_DIVISOR - deductFeePercentForNormal) / BASIS_POINTS_DIVISOR);
        } else {
            return (false, 0);
        }
    }

    function validateMaxOILimit(address _account, bool _isLong, uint256 _size, uint256 _tokenId) external view returns (uint256, uint256, uint256, uint8) {
        uint256 _openInterestPerUser = settingsManager.openInterestPerUser(_account);
        uint256 _maxOpenInterestPerUser = settingsManager.maxOpenInterestPerUser(_account);
        uint256 tradingFee = settingsManager.getTradingFee(_account, _tokenId, _isLong, _size);
        uint256 triggerGasFee = settingsManager.triggerGasFee();
        uint256 marketOrderGasFee = settingsManager.marketOrderGasFee();
        if (_maxOpenInterestPerUser == 0) _maxOpenInterestPerUser = settingsManager.defaultMaxOpenInterestPerUser();
        if (_openInterestPerUser + _size > _maxOpenInterestPerUser)
            return (triggerGasFee, marketOrderGasFee, tradingFee, 1);
        uint256 _openInterestPerAssetPerSide = settingsManager.openInterestPerAssetPerSide(_tokenId, _isLong);
        if (_openInterestPerAssetPerSide + _size > settingsManager.maxOpenInterestPerAssetPerSide(_tokenId, _isLong ))
            return (triggerGasFee, marketOrderGasFee, tradingFee, 2);
        return (triggerGasFee, marketOrderGasFee, tradingFee, 0);
    }

    function getUserBalances(address _account) external view returns (uint256 ethBalance, uint256 usdcBalance, uint256 usdcAllowance, uint256 vusdBalance) {
        ethBalance = _account.balance;
        usdcBalance = USDC.balanceOf(_account);
        usdcAllowance = USDC.allowance(_account, address(vault));
        vusdBalance = vUSD.balanceOf(_account);
    }

    function getUserVelaAndEsVelaInfo(address _account) external view returns (uint256[] memory, uint256[] memory, uint256[] memory, uint256[] memory, uint256[] memory) {
        uint256[] memory balances = new uint256[](2);
        uint256[] memory allowances = new uint256[](2);
        uint256[] memory stakedAmounts = new uint256[](2);
        balances[0] = vela.balanceOf(_account);
        balances[1] = esVela.balanceOf(_account);
        allowances[0] = vela.allowance(_account, address(tokenFarm));
        allowances[1] = esVela.allowance(_account, address(tokenFarm));
        (uint256 velaStakedAmount, uint256 esVelaStakedAmount) = tokenFarm.getStakedVela(_account);
        stakedAmounts[0] = velaStakedAmount;
        stakedAmounts[1] = esVelaStakedAmount;
        (, , uint256[] memory decimals, uint256[] memory pendingTokens) = tokenFarm.pendingTokens(true, _account);
        return (balances, allowances, decimals, pendingTokens, stakedAmounts);
    }

    function getUserVLPInfo(address _account) external view returns (uint256[] memory, uint256[] memory, uint256[] memory) {
        uint256 balance = vlp.balanceOf(_account);
        uint256 allowance = vlp.allowance(_account, address(tokenFarm));
        uint256 vlpPrice = vault.getVLPPrice();
        (uint256 vlpStakedAmount, uint256 startTimestamp) = tokenFarm.getStakedVLP(_account);
        uint256 cooldownDuration = tokenFarm.cooldownDuration();
        uint256[] memory vlpInfo = new uint256[](6);
        vlpInfo[0] = balance;
        vlpInfo[1] = allowance;
        vlpInfo[2] = vlpStakedAmount;
        vlpInfo[3] = vlpPrice;
        vlpInfo[4] = startTimestamp;
        vlpInfo[5] = cooldownDuration;
        (, , uint256[] memory decimals, uint256[] memory pendingTokens) = tokenFarm.pendingTokens(false, _account);
        return (vlpInfo, decimals, pendingTokens);
    }

    function getUserVestingInfo(address _account) external view returns (uint256, uint256) {
        uint256 totalVestedAmount = tokenFarm.getTotalVested(_account);
        uint256 vestingClaimableAmount = tokenFarm.claimable(_account);
        return (totalVestedAmount, vestingClaimableAmount);
    }
}

contracts/core/ReaderV2.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IPositionVault.sol";
import "./interfaces/IOrderVault.sol";
import "./interfaces/IVault.sol";
import "./interfaces/ISettingsManager.sol";

import "../staking/interfaces/ITokenFarm.sol";

import {Constants} from "../access/Constants.sol";
import {OrderStatus, PositionTrigger, TriggerInfo, PaidFees} from "./structs.sol";

contract ReaderV2 is Constants, Initializable {
    struct AccruedFees {
        uint256 positionFee;
        uint256 borrowFee;
        int256 fundingFee;
    }

    IOrderVault private orderVault;
    IPositionVault private positionVault;
    ISettingsManager private settingsManager;
    ITokenFarm private tokenFarm;
    IVault private vault;
    IERC20 private USDC;
    IERC20 private vUSD;
    IERC20 private vlp;
    IERC20 private vela;
    IERC20 private esVela;

    function initialize(
        address _vault,
        address _positionVault,
        address _orderVault,
        address _settingsManager,
        address _tokenFarm,
        address _esVela,
        address _vela,
        address _vlp,
        address _usdc,
        address _vusd
    ) public initializer {
        vault = IVault(_vault);
        positionVault = IPositionVault(_positionVault);
        orderVault = IOrderVault(_orderVault);
        settingsManager = ISettingsManager(_settingsManager);
        tokenFarm = ITokenFarm(_tokenFarm);
        esVela = IERC20(_esVela);
        vela = IERC20(_vela);
        vlp = IERC20(_vlp);
        USDC = IERC20(_usdc);
        vUSD = IERC20(_vusd);
    }

    function getUserAlivePositions(
        address _user
    )
        public
        view
        returns (uint256[] memory, Position[] memory, Order[] memory, PositionTrigger[] memory, PaidFees[] memory, AccruedFees[] memory)
    {
        uint256[] memory posIds = positionVault.getUserPositionIds(_user);
        uint256 length = posIds.length;
        Position[] memory positions_ = new Position[](length);
        Order[] memory orders_ = new Order[](length);
        PositionTrigger[] memory triggers_ = new PositionTrigger[](length);
        PaidFees[] memory paidFees_ = new PaidFees[](length);
        AccruedFees[] memory accruedFees_ = new AccruedFees[](length);
        for (uint i; i < length; ++i) {
            uint256 posId = posIds[i];
            positions_[i] = positionVault.getPosition(posId);
            orders_[i] = orderVault.getOrder(posId);
            triggers_[i] = orderVault.getTriggerOrderInfo(posId);
            paidFees_[i] = positionVault.getPaidFees(posId);
            accruedFees_[i] = getAccruedFee(posId);
        }
        return (posIds, positions_, orders_, triggers_, paidFees_, accruedFees_);
    }

    function getAccruedFee(uint256 _posId) internal view returns (AccruedFees memory){
        Position memory position = positionVault.getPosition(_posId);
        AccruedFees memory accruedFees;
        accruedFees.positionFee = settingsManager.getTradingFee(position.owner, position.tokenId, position.isLong, position.size);
        accruedFees.borrowFee = settingsManager.getBorrowFee(position.size, position.lastIncreasedTime, position.tokenId, position.isLong) + position.accruedBorrowFee;
        accruedFees.fundingFee = settingsManager.getFundingFee(position.tokenId, position.isLong, position.size, position.fundingIndex);
        return accruedFees;
    }

    function getGlobalInfo(
        address _account,
        uint256 _tokenId
    )
        external
        view
        returns (
            int256 fundingRate,
            uint256 borrowRateForLong,
            uint256 borrowRateForShort,
            uint256 longOpenInterest,
            uint256 shortOpenInterest,
            uint256 maxLongOpenInterest,
            uint256 maxShortOpenInterest,
            uint256 longTradingFee,
            uint256 shortTradingFee
        )
    {
        fundingRate = settingsManager.getFundingRate(_tokenId);
        borrowRateForLong = settingsManager.getBorrowRate(_tokenId, true);
        borrowRateForShort = settingsManager.getBorrowRate(_tokenId, false);
        longOpenInterest = settingsManager.openInterestPerAssetPerSide(_tokenId, true);
        shortOpenInterest = settingsManager.openInterestPerAssetPerSide(_tokenId, false);
        maxLongOpenInterest = settingsManager.maxOpenInterestPerAssetPerSide(_tokenId, true);
        maxShortOpenInterest = settingsManager.maxOpenInterestPerAssetPerSide(_tokenId, false);
        longTradingFee = settingsManager.getTradingFee(_account, _tokenId, true, PRICE_PRECISION);
        shortTradingFee = settingsManager.getTradingFee(_account, _tokenId, false, PRICE_PRECISION);
    }

    function getUserOpenOrders(
        address _user
    )
        public
        view
        returns (uint256[] memory, Position[] memory, Order[] memory, PositionTrigger[] memory, PaidFees[] memory, AccruedFees[] memory)
    {
        uint256[] memory posIds = positionVault.getUserOpenOrderIds(_user);
        uint256 length = posIds.length;
        Position[] memory positions_ = new Position[](length);
        Order[] memory orders_ = new Order[](length);
        PositionTrigger[] memory triggers_ = new PositionTrigger[](length);
        PaidFees[] memory paidFees_ = new PaidFees[](length);
        AccruedFees[] memory accruedFees_ = new AccruedFees[](length);
        for (uint i; i < length; ++i) {
            uint256 posId = posIds[i];
            positions_[i] = positionVault.getPosition(posId);
            orders_[i] = orderVault.getOrder(posId);
            triggers_[i] = orderVault.getTriggerOrderInfo(posId);
            paidFees_[i] = positionVault.getPaidFees(posId);
            accruedFees_[i] = getAccruedFee(posId);
        }
        return (posIds, positions_, orders_, triggers_, paidFees_, accruedFees_);
    }

    function getFeesFor1CT(address _normal, address _oneCT) external view returns (bool, uint256) {
        uint256 tierVelaPercent = tokenFarm.getTierVela(_normal);
        uint256 deductFeePercentForNormal = settingsManager.deductFeePercent(_normal);
        uint256 deductFeePercentForOneCT = settingsManager.deductFeePercent(_oneCT);
        if (tierVelaPercent * (BASIS_POINTS_DIVISOR - deductFeePercentForNormal) / BASIS_POINTS_DIVISOR != (BASIS_POINTS_DIVISOR - deductFeePercentForOneCT)) {
            return (true, BASIS_POINTS_DIVISOR - tierVelaPercent * (BASIS_POINTS_DIVISOR - deductFeePercentForNormal) / BASIS_POINTS_DIVISOR);
        } else {
            return (false, 0);
        }
    }

    function validateMaxOILimit(address _account, bool _isLong, uint256 _size, uint256 _tokenId) external view returns (uint256, uint256, uint256, uint8) {
        uint256 _openInterestPerUser = settingsManager.openInterestPerUser(_account);
        uint256 _maxOpenInterestPerUser = settingsManager.maxOpenInterestPerUser(_account);
        uint256 tradingFee = settingsManager.getTradingFee(_account, _tokenId, _isLong, _size);
        uint256 triggerGasFee = settingsManager.triggerGasFee();
        uint256 marketOrderGasFee = settingsManager.marketOrderGasFee();
        if (_maxOpenInterestPerUser == 0) _maxOpenInterestPerUser = settingsManager.defaultMaxOpenInterestPerUser();
        if (_openInterestPerUser + _size > _maxOpenInterestPerUser)
            return (triggerGasFee, marketOrderGasFee, tradingFee, 1);
        uint256 _openInterestPerAssetPerSide = settingsManager.openInterestPerAssetPerSide(_tokenId, _isLong);
        if (_openInterestPerAssetPerSide + _size > settingsManager.maxOpenInterestPerAssetPerSide(_tokenId, _isLong ))
            return (triggerGasFee, marketOrderGasFee, tradingFee, 2);
        return (triggerGasFee, marketOrderGasFee, tradingFee, 0);
    }

    function getSpread(uint256 _tokenId, uint256 _size) external view returns (uint256) {
        uint256 slippageFactor = settingsManager.slippageFactor(_tokenId);
        uint256 spread = settingsManager.getSlippage(slippageFactor, _size);
        return spread;
    }

    function getUserBalances(address _account) external view returns (uint256 ethBalance, uint256 usdcBalance, uint256 usdcAllowance, uint256 vusdBalance) {
        ethBalance = _account.balance;
        usdcBalance = USDC.balanceOf(_account);
        usdcAllowance = USDC.allowance(_account, address(vault));
        vusdBalance = vUSD.balanceOf(_account);
    }

    function getUserVelaAndEsVelaInfo(address _account) external view returns (uint256[] memory, uint256[] memory, uint256[] memory, uint256[] memory, uint256[] memory) {
        uint256[] memory balances = new uint256[](2);
        uint256[] memory allowances = new uint256[](2);
        uint256[] memory stakedAmounts = new uint256[](2);
        balances[0] = vela.balanceOf(_account);
        balances[1] = esVela.balanceOf(_account);
        allowances[0] = vela.allowance(_account, address(tokenFarm));
        allowances[1] = esVela.allowance(_account, address(tokenFarm));
        (uint256 velaStakedAmount, uint256 esVelaStakedAmount) = tokenFarm.getStakedVela(_account);
        stakedAmounts[0] = velaStakedAmount;
        stakedAmounts[1] = esVelaStakedAmount;
        (, , uint256[] memory decimals, uint256[] memory pendingTokens) = tokenFarm.pendingTokens(true, _account);
        return (balances, allowances, decimals, pendingTokens, stakedAmounts);
    }

    function getUserVLPInfo(address _account) external view returns (uint256[] memory, uint256[] memory, uint256[] memory) {
        uint256 balance = vlp.balanceOf(_account);
        uint256 allowance = vlp.allowance(_account, address(tokenFarm));
        uint256 vlpPrice = vault.getVLPPrice();
        (uint256 vlpStakedAmount, uint256 startTimestamp) = tokenFarm.getStakedVLP(_account);
        uint256 cooldownDuration = tokenFarm.cooldownDuration();
        uint256[] memory vlpInfo = new uint256[](6);
        vlpInfo[0] = balance;
        vlpInfo[1] = allowance;
        vlpInfo[2] = vlpStakedAmount;
        vlpInfo[3] = vlpPrice;
        vlpInfo[4] = startTimestamp;
        vlpInfo[5] = cooldownDuration;
        (, , uint256[] memory decimals, uint256[] memory pendingTokens) = tokenFarm.pendingTokens(false, _account);
        return (vlpInfo, decimals, pendingTokens);
    }

    function getUserVestingInfo(address _account) external view returns (uint256, uint256) {
        uint256 totalVestedAmount = tokenFarm.getTotalVested(_account);
        uint256 vestingClaimableAmount = tokenFarm.claimable(_account);
        return (totalVestedAmount, vestingClaimableAmount);
    }
}

contracts/core/SettingsManager.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "./interfaces/ISettingsManager.sol";
import "./interfaces/ILiquidateVault.sol";
import "./interfaces/IPositionVault.sol";
import "./interfaces/IOperators.sol";
import "../staking/interfaces/ITokenFarm.sol";
import "../tokens/interfaces/IVUSD.sol";
import {Constants} from "../access/Constants.sol";

contract SettingsManager is ISettingsManager, Initializable, ReentrancyGuardUpgradeable, Constants {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    // constants
    ILiquidateVault public liquidateVault;
    IPositionVault public positionVault;
    ITokenFarm public tokenFarm;
    IOperators public operators;
    address public vusd;

    /* ========== VAULT SETTINGS ========== */
    uint256 public override cooldownDuration;
    mapping(address => bool) public override isWhitelistedFromCooldown;
    uint256 public override feeRewardBasisPoints;
    address public override feeManager;
    uint256 public override defaultMaxProfitPercent;

    event SetCooldownDuration(uint256 cooldownDuration);
    event SetIsWhitelistedFromCooldown(address addr, bool isWhitelisted);
    event SetIsWhitelistedFromTransferCooldown(address addr, bool isWhitelisted);
    event SetFeeRewardBasisPoints(uint256 feeRewardBasisPoints);
    event SetFeeManager(address indexed feeManager);
    event SetDefaultMaxProfitPercent(uint256 defaultMaxProfitPercent);
    event SetMaxProfitPercent(uint256 tokenId, uint256 maxProfitPercent);
    event SetMaxTotalVlp(uint256 maxTotalVlp);

    /* ========== VAULT SWITCH ========== */
    mapping(address => bool) public override isDeposit;
    mapping(address => bool) public override isWithdraw;
    mapping(address => bool) public override isStakingEnabled;
    mapping(address => bool) public override isUnstakingEnabled;

    event SetEnableDeposit(address indexed token, bool isEnabled);
    event SetEnableWithdraw(address indexed token, bool isEnabled);
    event SetEnableStaking(address indexed token, bool isEnabled);
    event SetEnableUnstaking(address indexed token, bool isEnabled);

    /* ========== VAULT FEE ========== */
    mapping(address => uint256) public override depositFee;
    mapping(address => uint256) public override withdrawFee;
    mapping(address => uint256) public override stakingFee;
    mapping(address => uint256) public override unstakingFee;

    event SetDepositFee(address indexed token, uint256 indexed fee);
    event SetWithdrawFee(address indexed token, uint256 indexed fee);
    event SetStakingFee(address indexed token, uint256 indexed fee);
    event SetUnstakingFee(address indexed token, uint256 indexed fee);

    /* ========== TRADING FEE ========== */
    mapping(uint256 => mapping(bool => uint256)) public override tradingFee; // 100 = 0.1%
    mapping(address => uint256) public override deductFeePercent;

    event SetTradingFee(uint256 indexed tokenId, bool isLong, uint256 tradingFee);
    event SetDeductFeePercent(address indexed account, uint256 deductFee);

    /* ========== FUNDING FEE ========== */
    uint256 public override basisFundingRateFactor;
    mapping(uint256 => uint256) public override fundingRateFactor;
    uint256 public override maxFundingRate;

    event SetBasisFundingRateFactor(uint256 basisFundingRateFactor);
    event SetFundingRateFactor(uint256 indexed tokenId, uint256 fundingRateFactor);
    event SetMaxFundingRate(uint256 maxFundingRateFactor);

    event SetVolatilityFactor(uint256 indexed tokenId, uint256 volatilityFactor);
    event SetLongBiasFactor(uint256 indexed tokenId, uint256 longBiasFactor);
    event SetFundingRateVelocityFactor(uint256 indexed tokenId, uint256 fundingRateVelocityFactor);
    event SetTempMaxFundingRateFactor(uint256 indexed tokenId, uint256 tempMaxFundingRateFactor);

    mapping(uint256 => int256) public override fundingIndex;
    mapping(uint256 => uint256) public override lastFundingTimes;

    event UpdateFunding(uint256 indexed tokenId, int256 fundingIndex);

    /* ========== BORROW FEE ========== */
    uint256 public override defaultBorrowFeeFactor; // deprecated
    mapping(uint256 => uint256) public override borrowFeeFactor; // deprecated

    event SetBorrowFeeFactorPerAssetPerSide(uint256 tokenId, bool isLong, uint256 borrowFeeFactor);

    /* ========== REFER FEE ========== */
    mapping(address => uint256) public override referrerTiers;
    mapping(uint256 => uint256) public override tierFees;

    event SetReferrerTier(address referrer, uint256 tier);
    event SetTierFee(uint256 tier, uint256 fee);
    event SetTierRebate(uint256 tier, uint256 rebate);
    event SetPlatformFee(address platform, uint256 fee);

    /* ========== INCREASE/DECREASE POSITION ========== */
    mapping(uint256 => bool) public override isIncreasingPositionDisabled;
    mapping(uint256 => bool) public override isDecreasingPositionDisabled;
    uint256 public override minCollateral;
    uint256 public override closeDeltaTime;

    event SetIsIncreasingPositionDisabled(uint256 tokenId, bool isDisabled);
    event SetIsDecreasingPositionDisabled(uint256 tokenId, bool isDisabled);
    event SetMinCollateral(uint256 minCollateral);
    event SetCloseDeltaTime(uint256 deltaTime);
    event SetMinProfitDuration(uint256 tokenId, uint256 minProfitDuration);
    event SetMaxCloseProfit(uint256 tokenId, uint256 maxCloseProfit);
    event SetMaxCloseProfitPercent(uint256 tokenId, uint256 maxCloseProfitPercent);

    /* ========== OPEN INTEREST MECHANISM ========== */
    uint256 public defaultMaxOpenInterestPerUser;
    mapping(address => uint256) public maxOpenInterestPerUser;
    mapping(uint256 => mapping(bool => uint256)) public maxOpenInterestPerAssetPerSide;

    event SetDefaultMaxOpenInterestPerUser(uint256 maxOIAmount);
    event SetMaxOpenInterestPerUser(address indexed account, uint256 maxOIAmount);
    event SetMaxOpenInterestPerAssetPerSide(uint256 indexed tokenId, bool isLong, uint256 maxOIAmount);
    event SetMaxTotalOpenInterest(uint256 maxOIAmount);

    mapping(address => uint256) public override openInterestPerUser;
    mapping(uint256 => mapping(bool => uint256)) public override openInterestPerAssetPerSide;
    uint256 public override totalOpenInterest;

    event IncreaseOpenInterest(uint256 indexed id, bool isLong, uint256 amount);
    event DecreaseOpenInterest(uint256 indexed id, bool isLong, uint256 amount);

    /* ========== MARKET ORDER ========== */
    uint256 public override marketOrderGasFee;
    uint256 public override expiryDuration;
    uint256 public override selfExecuteCooldown;

    event SetMarketOrderGasFee(uint256 indexed fee);
    event SetExpiryDuration(uint256 expiryDuration);
    event SetSelfExecuteCooldown(uint256 selfExecuteCooldown);

    /* ========== TRIGGER ORDER ========== */
    uint256 public override triggerGasFee;
    uint256 public override maxTriggerPerPosition;
    uint256 public override priceMovementPercent;

    event SetTriggerGasFee(uint256 indexed fee);
    event SetMaxTriggerPerPosition(uint256 value);
    event SetPriceMovementPercent(uint256 priceMovementPercent);

    /* ========== ARTIFICIAL SLIPPAGE MECHANISM ========== */
    mapping(uint256 => uint256) public override slippageFactor;

    event SetSlippageFactor(uint256 indexed tokenId, uint256 slippageFactor);

    /* ========== LIQUIDATE MECHANISM ========== */
    mapping(uint256 => uint256) public liquidateThreshold;
    uint256 public override liquidationPendingTime;
    uint256 private unused; // removal of liquidationFee
    struct BountyPercent {
        uint32 firstCaller;
        uint32 resolver;
    } // pack to save gas
    BountyPercent private bountyPercent_;

    event SetLiquidateThreshold(uint256 indexed tokenId, uint256 newThreshold);
    event SetLiquidationPendingTime(uint256 liquidationPendingTime);
    event SetBountyPercent(uint32 bountyPercentFirstCaller, uint32 bountyPercentResolver);

    /* ========== DELEGATE MECHANISM========== */
    mapping(address => EnumerableSetUpgradeable.AddressSet) private _delegatesByMaster;
    mapping(address => bool) public globalDelegates; // treat these addrs already be delegated

    event GlobalDelegatesChange(address indexed delegate, bool allowed);

    /* ========== BAN MECHANISM========== */
    EnumerableSetUpgradeable.AddressSet private banWalletList;

    /* new variables */
    mapping(address => bool) public override isWhitelistedFromTransferCooldown;
    mapping(uint256 => uint256) public override maxProfitPercent;
    uint256 public maxTotalOpenInterest;
    mapping(uint256 => mapping(bool => uint256)) public borrowFeeFactorPerAssetPerSide;
    mapping(uint256 => uint256) public tierRebates; // tier => rebate percent for trader
    mapping(address => uint256) public override platformFees; // address of 3rd platform to receive platform fee => fee percent
    uint256 public override maxTotalVlp;

    mapping(uint256 => uint256) public override minProfitDurations; // tokenId => minProfitDuration
    mapping(uint256 => uint256) public override maxCloseProfits; // tokenId => maxCloseProfit
    mapping(uint256 => uint256) public override maxCloseProfitPercents; // tokenId => maxCloseProfitPercent

    mapping(uint256 => int256) public override lastFundingRates; // tokenId => last updated funding rate
    mapping(uint256 => uint256) public override volatilityFactors; // tokenId => volatilityFactor
    mapping(uint256 => uint256) public override longBiasFactors; // tokenId => longBiasFactors
    mapping(uint256 => uint256) public override fundingRateVelocityFactors; // tokenId => fundingRateVelocityFactor
    mapping(uint256 => uint256) public override tempMaxFundingRateFactors; // tokenId => tempMaxFundingRateFactor

    /* ========== MODIFIERS ========== */
    modifier onlyVault() {
        require(msg.sender == address(positionVault) || msg.sender == address(liquidateVault), "Only vault");
        _;
    }

    modifier onlyOperator(uint256 level) {
        _onlyOperator(level);
        _;
    }

    function _onlyOperator(uint256 level) private view {
        require(operators.getOperatorLevel(msg.sender) >= level, "invalid operator");
    }

    /* ========== INITIALIZE FUNCTION========== */
    function initialize(
        address _liquidateVault,
        address _positionVault,
        address _operators,
        address _vusd,
        address _tokenFarm
    ) public initializer {
        __ReentrancyGuard_init();
        liquidateVault = ILiquidateVault(_liquidateVault);
        positionVault = IPositionVault(_positionVault);
        operators = IOperators(_operators);
        tokenFarm = ITokenFarm(_tokenFarm);
        vusd = _vusd;
        priceMovementPercent = 50; // 0.05%
        defaultMaxProfitPercent = 10000; // 10%
        bountyPercent_ = BountyPercent({firstCaller: 20000, resolver: 50000}); // first caller 20%, resolver 50% and leftover to team
        liquidationPendingTime = 10; // allow 10 seconds for manager to resolve liquidation
        cooldownDuration = 3 hours;
        expiryDuration = 60; // 60 seconds
        selfExecuteCooldown = 60; // 60 seconds
        feeRewardBasisPoints = 50000; // 50%
        minCollateral = 5 * PRICE_PRECISION; // min 5 USD
        defaultBorrowFeeFactor = 10; // 0.01% per hour
        triggerGasFee = 0; //100 gwei;
        marketOrderGasFee = 0;
        basisFundingRateFactor = 10000;
        tierFees[0] = 5000; // 5% refer fee for default tier
        maxTriggerPerPosition = 10;
        defaultMaxOpenInterestPerUser = 10000000000000000 * PRICE_PRECISION;
        maxFundingRate = FUNDING_RATE_PRECISION / 100; // 1% per hour
        maxTotalOpenInterest = 10000000000 * PRICE_PRECISION;
        unused = 10000; // 10%
        maxTotalVlp = 20 * 10 ** 6 * 10 ** VLP_DECIMALS; // 20mil max vlp supply
    }

    function initializeV2() public reinitializer(2) {
        bountyPercent_ = BountyPercent({firstCaller: 2000, resolver: 8000}); // first caller 2%, resolver 8% and leftover 90% to vlp
    }

    function initializeV3() public reinitializer(3) {
        maxTotalOpenInterest = 10000000000 * PRICE_PRECISION;
        defaultBorrowFeeFactor = 100; // 0.01% per hour
    }

    /* ========== VAULT SETTINGS ========== */
    /* OP FUNCTIONS */
    function setCooldownDuration(uint256 _cooldownDuration) external onlyOperator(3) {
        require(_cooldownDuration <= MAX_COOLDOWN_DURATION, "invalid cooldownDuration");
        cooldownDuration = _cooldownDuration;
        emit SetCooldownDuration(_cooldownDuration);
    }

    function setIsWhitelistedFromCooldown(address _addr, bool _isWhitelisted) external onlyOperator(3) {
        isWhitelistedFromCooldown[_addr] = _isWhitelisted;
        emit SetIsWhitelistedFromCooldown(_addr, _isWhitelisted);
    }

    function setIsWhitelistedFromTransferCooldown(address _addr, bool _isWhitelisted) external onlyOperator(3) {
        isWhitelistedFromTransferCooldown[_addr] = _isWhitelisted;
        emit SetIsWhitelistedFromTransferCooldown(_addr, _isWhitelisted);
    }

    function setFeeRewardBasisPoints(uint256 _feeRewardsBasisPoints) external onlyOperator(3) {
        require(_feeRewardsBasisPoints <= BASIS_POINTS_DIVISOR, "Above max");
        feeRewardBasisPoints = _feeRewardsBasisPoints;
        emit SetFeeRewardBasisPoints(_feeRewardsBasisPoints);
    }

    function setFeeManager(address _feeManager) external onlyOperator(3) {
        feeManager = _feeManager;
        emit SetFeeManager(_feeManager);
    }

    function setDefaultMaxProfitPercent(uint256 _defaultMaxProfitPercent) external onlyOperator(3) {
        defaultMaxProfitPercent = _defaultMaxProfitPercent;
        emit SetDefaultMaxProfitPercent(_defaultMaxProfitPercent);
    }

    function setMaxProfitPercent(uint256 _tokenId, uint256 _maxProfitPercent) external onlyOperator(3) {
        maxProfitPercent[_tokenId] = _maxProfitPercent;
        emit SetMaxProfitPercent(_tokenId, _maxProfitPercent);
    }

    function setMaxTotalVlp(uint256 _maxTotalVlp) external onlyOperator(3) {
        require(_maxTotalVlp > 0, "invalid maxTotalVlp");
        maxTotalVlp = _maxTotalVlp;
        emit SetMaxTotalVlp(_maxTotalVlp);
    }

    /* ========== VAULT SWITCH ========== */
    /* OP FUNCTIONS */
    function setEnableDeposit(address _token, bool _isEnabled) external onlyOperator(3) {
        isDeposit[_token] = _isEnabled;
        emit SetEnableDeposit(_token, _isEnabled);
    }

    function setEnableWithdraw(address _token, bool _isEnabled) external onlyOperator(3) {
        isWithdraw[_token] = _isEnabled;
        emit SetEnableWithdraw(_token, _isEnabled);
    }

    function setEnableStaking(address _token, bool _isEnabled) external onlyOperator(3) {
        isStakingEnabled[_token] = _isEnabled;
        emit SetEnableStaking(_token, _isEnabled);
    }

    function setEnableUnstaking(address _token, bool _isEnabled) external onlyOperator(3) {
        isUnstakingEnabled[_token] = _isEnabled;
        emit SetEnableUnstaking(_token, _isEnabled);
    }

    /* ========== VAULT FEE ========== */
    /* OP FUNCTIONS */
    function setDepositFee(address token, uint256 _fee) external onlyOperator(3) {
        require(_fee <= MAX_DEPOSIT_WITHDRAW_FEE, "Above max");
        depositFee[token] = _fee;
        emit SetDepositFee(token, _fee);
    }

    function setWithdrawFee(address token, uint256 _fee) external onlyOperator(3) {
        require(_fee <= MAX_DEPOSIT_WITHDRAW_FEE, "Above max");
        withdrawFee[token] = _fee;
        emit SetWithdrawFee(token, _fee);
    }

    function setStakingFee(address token, uint256 _fee) external onlyOperator(3) {
        require(_fee <= MAX_STAKING_UNSTAKING_FEE, "Above max");
        stakingFee[token] = _fee;
        emit SetStakingFee(token, _fee);
    }

    function setUnstakingFee(address token, uint256 _fee) external onlyOperator(3) {
        require(_fee <= MAX_STAKING_UNSTAKING_FEE, "Above max");
        unstakingFee[token] = _fee;
        emit SetUnstakingFee(token, _fee);
    }

    /* ========== TRADING FEE ========== */
    /* OP FUNCTIONS */
    function setTradingFee(uint256 _tokenId, bool _isLong, uint256 _tradingFee) external onlyOperator(3) {
        require(_tradingFee <= MAX_FEE_BASIS_POINTS, "Above max");
        tradingFee[_tokenId][_isLong] = _tradingFee;
        emit SetTradingFee(_tokenId, _isLong, _tradingFee);
    }

    function setDeductFeePercentForUser(address _account, uint256 _deductFee) external onlyOperator(2) {
        require(_deductFee <= BASIS_POINTS_DIVISOR, "Above max");
        deductFeePercent[_account] = _deductFee;
        emit SetDeductFeePercent(_account, _deductFee);
    }

    /* VIEW FUNCTIONS */
    function getTradingFee(
        address _account,
        uint256 _tokenId,
        bool _isLong,
        uint256 _sizeDelta
    ) external view override returns (uint256) {
        return
            (getUndiscountedTradingFee(_tokenId, _isLong, _sizeDelta) *
                (BASIS_POINTS_DIVISOR - deductFeePercent[_account]) *
                tokenFarm.getTierVela(_account)) / BASIS_POINTS_DIVISOR ** 2;
    }

    function getUndiscountedTradingFee(
        uint256 _tokenId,
        bool _isLong,
        uint256 _sizeDelta
    ) public view override returns (uint256) {
        return (_sizeDelta * tradingFee[_tokenId][_isLong]) / BASIS_POINTS_DIVISOR;
    }

    /* ========== FUNDING FEE ========== */
    /* OP FUNCTIONS */
    function setBasisFundingRateFactor(uint256 _basisFundingRateFactor) external onlyOperator(3) {
        basisFundingRateFactor = _basisFundingRateFactor;
        emit SetBasisFundingRateFactor(_basisFundingRateFactor);
    }

    function setFundingRateFactor(uint256 _tokenId, uint256 _fundingRateFactor) external onlyOperator(3) {
        fundingRateFactor[_tokenId] = _fundingRateFactor;
        emit SetFundingRateFactor(_tokenId, _fundingRateFactor);
    }

    function setMaxFundingRate(uint256 _maxFundingRate) external onlyOperator(3) {
        require(_maxFundingRate <= MAX_FUNDING_RATE, "Above max");
        maxFundingRate = _maxFundingRate;
        emit SetMaxFundingRate(_maxFundingRate);
    }

    function setVolatilityFactor(uint256 _tokenId, uint256 _volatilityFactor) external onlyOperator(3) {
        volatilityFactors[_tokenId] = _volatilityFactor;
        emit SetVolatilityFactor(_tokenId, _volatilityFactor);
    }

    function setLongBiasFactor(uint256 _tokenId, uint256 _longBiasFactor) external onlyOperator(3) {
        longBiasFactors[_tokenId] = _longBiasFactor;
        emit SetLongBiasFactor(_tokenId, _longBiasFactor);
    }

    function setFundingRateVelocityFactor(
        uint256 _tokenId,
        uint256 _fundingRateVelocityFactor
    ) external onlyOperator(3) {
        fundingRateVelocityFactors[_tokenId] = _fundingRateVelocityFactor;
        emit SetFundingRateVelocityFactor(_tokenId, _fundingRateVelocityFactor);
    }

    function setTempMaxFundingRateFactor(uint256 _tokenId, uint256 _tempMaxFundingRateFactor) external onlyOperator(3) {
        tempMaxFundingRateFactors[_tokenId] = _tempMaxFundingRateFactor;
        emit SetTempMaxFundingRateFactor(_tokenId, _tempMaxFundingRateFactor);
    }

    /* VAULT FUNCTIONS */
    // to update the fundingIndex every time before open interest changes
    function updateFunding(uint256 _tokenId) external override {
        if (lastFundingTimes[_tokenId] == 0) {
            require(msg.sender == address(positionVault), "initialized by vault only"); // can be initialized by vault only
        } else {
            int256 fundingRate = getFundingRate(_tokenId);
            int256 latestFundingIndex = fundingIndex[_tokenId] + getFundingChange(_tokenId, fundingRate);
            fundingIndex[_tokenId] = latestFundingIndex;

            emit UpdateFunding(_tokenId, latestFundingIndex);

            lastFundingRates[_tokenId] = fundingRate;
        }

        lastFundingTimes[_tokenId] = block.timestamp;
    }

    /* VIEW FUNCTIONS */
    // calculate fundingFee based on fundingIndex difference
    function getFundingFee(
        uint256 _tokenId,
        bool _isLong,
        uint256 _size,
        int256 _fundingIndex
    ) public view override returns (int256) {
        return
            _isLong
                ? (int256(_size) * (getLatestFundingIndex(_tokenId) - _fundingIndex)) / int256(FUNDING_RATE_PRECISION)
                : (int256(_size) * (_fundingIndex - getLatestFundingIndex(_tokenId))) / int256(FUNDING_RATE_PRECISION);
    }

    // calculate latestFundingIndex based on fundingChange
    function getLatestFundingIndex(uint256 _tokenId) public view returns (int256) {
        int256 fundingRate = getFundingRate(_tokenId);
        return fundingIndex[_tokenId] + getFundingChange(_tokenId, fundingRate);
    }

    // calculate fundingChange based on fundingRate and period it has taken effect
    // averageFundingRate is used to calc fundingChange as the fundingRate slowly changes over time due to fundingRateVelocity
    function getFundingChange(uint256 _tokenId, int256 _fundingRate) private view returns (int256) {
        uint256 interval = block.timestamp - lastFundingTimes[_tokenId];
        if (interval == 0) return int256(0);

        int256 lastFundingRate = lastFundingRates[_tokenId];
        int256 averageFundingRate = (lastFundingRate + _fundingRate) / 2;

        return (averageFundingRate * int256(interval)) / int256(1 hours);
    }

    // get temporary max funding rate with 1e15 decimals
    function getTempMaxFundingRate(uint256 _tokenId) public view returns (uint256) {
        return (tempMaxFundingRateFactors[_tokenId] * volatilityFactors[_tokenId]) * BASIS_POINTS_DIVISOR;
    }

    // calculate funding rate per hour with 1e15 decimals
    function getFundingRate(uint256 _tokenId) public view override returns (int256) {
        uint256 interval = block.timestamp - lastFundingTimes[_tokenId];
        int256 fundingRate = lastFundingRates[_tokenId] +
            (int256(interval) * getFundingRateVelocity(_tokenId)) /
            int256(1 hours);

        uint256 tempMaxFundingRate = getTempMaxFundingRate(_tokenId);
        uint256 curMaxFundingRate = tempMaxFundingRate < maxFundingRate ? tempMaxFundingRate : maxFundingRate;

        if (fundingRate == 0) return 0;

        if (fundingRate > 0) {
            int256 _curMaxFundingRate = int256(curMaxFundingRate);
            if (fundingRate > _curMaxFundingRate) return _curMaxFundingRate;
        } else {
            int256 _curMaxFundingRate = -1 * int256(curMaxFundingRate);
            if (fundingRate < _curMaxFundingRate) return _curMaxFundingRate;
        }

        return fundingRate;
    }

    // calculate velocity for funding rate with 1e15 decimals
    function getFundingRateVelocity(uint256 _tokenId) public view returns (int256) {
        uint256 longOI = openInterestPerAssetPerSide[_tokenId][true];
        uint256 shortOI = openInterestPerAssetPerSide[_tokenId][false];
        uint256 limitOI = maxOpenInterestPerAssetPerSide[_tokenId][true] +
            maxOpenInterestPerAssetPerSide[_tokenId][false];
        if (limitOI == 0) return 0;

        // skewRatio = (longOI - shortOI) / limitOI
        int256 skewRatio = (int256(BASIS_POINTS_DIVISOR) * (int256(longOI) - int256(shortOI))) / int256(limitOI);

        int256 tempMaxFundingRate = int256(getTempMaxFundingRate(_tokenId));

        int256 fundingRateVelocityFactor = int256(fundingRateVelocityFactors[_tokenId]);
        if (fundingRateVelocityFactor == 0) return 0;

        // fundingRateVelocity = [tempMaxFundingRate * (skewRatio + longBias) - lastFundingRate] / fundingRateVelocityFactor
        return
            (tempMaxFundingRate *
                (skewRatio + int256(longBiasFactors[_tokenId])) -
                lastFundingRates[_tokenId] *
                int256(BASIS_POINTS_DIVISOR)) / fundingRateVelocityFactor;
    }

    /* ========== BORROW FEE ========== */
    /* OP FUNCTIONS */
    function setBorrowFeeFactorPerAssetPerSide(
        uint256 _tokenId,
        bool _isLong,
        uint256 _borrowFeeFactor
    ) external onlyOperator(3) {
        require(_borrowFeeFactor <= MAX_BORROW_FEE_FACTOR * 10, "Above max");
        borrowFeeFactorPerAssetPerSide[_tokenId][_isLong] = _borrowFeeFactor;
        emit SetBorrowFeeFactorPerAssetPerSide(_tokenId, _isLong, _borrowFeeFactor);
    }

    /* VIEW FUNCTIONS */
    function getBorrowFee(
        uint256 _borrowedSize,
        uint256 _lastIncreasedTime,
        uint256 _tokenId,
        bool _isLong
    ) public view override returns (uint256) {
        return
            ((block.timestamp - _lastIncreasedTime) * _borrowedSize * getBorrowRate(_tokenId, _isLong)) /
            (BASIS_POINTS_DIVISOR * 10) /
            1 hours;
    }

    // get borrow rate per hour with 1e6 decimals
    function getBorrowRate(uint256 _tokenId, bool _isLong) public view returns (uint256) {
        return borrowFeeFactorPerAssetPerSide[_tokenId][_isLong];
    }

    /* ========== REFER FEE ========== */
    /* OP FUNCTIONS */
    function setReferrerTier(address _referrer, uint256 _tier) external onlyOperator(1) {
        referrerTiers[_referrer] = _tier;
        emit SetReferrerTier(_referrer, _tier);
    }

    function setTierFee(uint256 _tier, uint256 _fee) external onlyOperator(3) {
        require(_fee + tierRebates[_tier] <= BASIS_POINTS_DIVISOR, "Above max");
        tierFees[_tier] = _fee;
        emit SetTierFee(_tier, _fee);
    }

    function setTierRebate(uint256 _tier, uint256 _rebate) external onlyOperator(3) {
        require(_rebate + tierFees[_tier] <= BASIS_POINTS_DIVISOR, "Above max");
        tierRebates[_tier] = _rebate;
        emit SetTierRebate(_tier, _rebate);
    }

    function setPlatformFee(address _platform, uint256 _fee) external onlyOperator(3) {
        require(_fee <= BASIS_POINTS_DIVISOR, "Above max");
        platformFees[_platform] = _fee;
        emit SetPlatformFee(_platform, _fee);
    }

    /* VIEW FUNCTIONS */
    function getReferFee(address _refer) external view override returns (uint256) {
        return tierFees[referrerTiers[_refer]];
    }

    function getTraderRebate(address _refer) external view returns (uint256) {
        return tierRebates[referrerTiers[_refer]];
    }

    function getReferFeeAndTraderRebate(
        address _refer
    ) external view override returns (uint256 referFee, uint256 traderRebate) {
        uint256 tier = referrerTiers[_refer];

        referFee = tierFees[tier];
        traderRebate = tierRebates[tier];
    }

    /* ========== INCREASE/DECREASE POSITION ========== */
    /* OP FUNCTIONS */
    function setIsIncreasingPositionDisabled(uint256 _tokenId, bool _isDisabled) external onlyOperator(2) {
        isIncreasingPositionDisabled[_tokenId] = _isDisabled;
        emit SetIsIncreasingPositionDisabled(_tokenId, _isDisabled);
    }

    function setIsDecreasingPositionDisabled(uint256 _tokenId, bool _isDisabled) external onlyOperator(2) {
        isDecreasingPositionDisabled[_tokenId] = _isDisabled;
        emit SetIsDecreasingPositionDisabled(_tokenId, _isDisabled);
    }

    function setMinCollateral(uint256 _minCollateral) external onlyOperator(3) {
        minCollateral = _minCollateral;
        emit SetMinCollateral(_minCollateral);
    }

    function setCloseDeltaTime(uint256 _deltaTime) external onlyOperator(2) {
        require(_deltaTime <= MAX_DELTA_TIME, "Above max");
        closeDeltaTime = _deltaTime;
        emit SetCloseDeltaTime(_deltaTime);
    }

    function setMinProfitDuration(uint256 _tokenId, uint256 _minProfitDuration) external onlyOperator(3) {
        minProfitDurations[_tokenId] = _minProfitDuration;
        emit SetMinProfitDuration(_tokenId, _minProfitDuration);
    }

    function setMaxCloseProfit(uint256 _tokenId, uint256 _maxCloseProfit) external onlyOperator(3) {
        maxCloseProfits[_tokenId] = _maxCloseProfit;
        emit SetMaxCloseProfit(_tokenId, _maxCloseProfit);
    }

    function setMaxCloseProfitPercent(uint256 _tokenId, uint256 _maxCloseProfitPercent) external onlyOperator(3) {
        maxCloseProfitPercents[_tokenId] = _maxCloseProfitPercent;
        emit SetMaxCloseProfitPercent(_tokenId, _maxCloseProfitPercent);
    }

    /* VIEW FUNCTIONS */
    function getPnl(
        uint256 _tokenId,
        bool _isLong,
        uint256 _size,
        uint256 _averagePrice,
        uint256 _lastPrice,
        uint256 _lastIncreasedTime,
        uint256 _accruedBorrowFee,
        int256 _fundingIndex
    ) external view override returns (int256 pnl, int256 fundingFee, int256 borrowFee) {
        require(_averagePrice > 0, "avgPrice > 0");

        if (_isLong) {
            if (_lastPrice >= _averagePrice) {
                pnl = int256((_size * (_lastPrice - _averagePrice)) / _averagePrice);
            } else {
                pnl = -1 * int256((_size * (_averagePrice - _lastPrice)) / _averagePrice);
            }
        } else {
            if (_lastPrice <= _averagePrice) {
                pnl = int256((_size * (_averagePrice - _lastPrice)) / _averagePrice);
            } else {
                pnl = -1 * int256((_size * (_lastPrice - _averagePrice)) / _averagePrice);
            }
        }

        fundingFee = getFundingFee(_tokenId, _isLong, _size, _fundingIndex);
        borrowFee = int256(getBorrowFee(_size, _lastIncreasedTime, _tokenId, _isLong) + _accruedBorrowFee);

        pnl = pnl - fundingFee - borrowFee;
    }

    /* ========== OPEN INTEREST MECHANISM ========== */
    /* OP FUNCTIONS */
    function setDefaultMaxOpenInterestPerUser(uint256 _maxAmount) external onlyOperator(1) {
        defaultMaxOpenInterestPerUser = _maxAmount;
        emit SetDefaultMaxOpenInterestPerUser(_maxAmount);
    }

    function setMaxOpenInterestPerUser(address _account, uint256 _maxAmount) external onlyOperator(2) {
        maxOpenInterestPerUser[_account] = _maxAmount;
        emit SetMaxOpenInterestPerUser(_account, _maxAmount);
    }

    function setMaxOpenInterestPerAsset(uint256 _tokenId, uint256 _maxAmount) external override onlyOperator(2) {
        setMaxOpenInterestPerAssetPerSide(_tokenId, true, _maxAmount);
        setMaxOpenInterestPerAssetPerSide(_tokenId, false, _maxAmount);
    }

    function setMaxOpenInterestPerAssetPerSide(
        uint256 _tokenId,
        bool _isLong,
        uint256 _maxAmount
    ) public onlyOperator(2) {
        maxOpenInterestPerAssetPerSide[_tokenId][_isLong] = _maxAmount;
        emit SetMaxOpenInterestPerAssetPerSide(_tokenId, _isLong, _maxAmount);
    }

    function setMaxTotalOpenInterest(uint256 _maxAmount) external onlyOperator(2) {
        maxTotalOpenInterest = _maxAmount;
        emit SetMaxTotalOpenInterest(_maxAmount);
    }

    /* VAULT FUNCTIONS */
    function increaseOpenInterest(
        uint256 _tokenId,
        address _sender,
        bool _isLong,
        uint256 _amount
    ) external override onlyVault {
        // check and increase openInterestPerUser
        uint256 _openInterestPerUser = openInterestPerUser[_sender];
        uint256 _maxOpenInterestPerUser = maxOpenInterestPerUser[_sender];
        if (_maxOpenInterestPerUser == 0) _maxOpenInterestPerUser = defaultMaxOpenInterestPerUser;
        require(_openInterestPerUser + _amount <= _maxOpenInterestPerUser, "user maxOI exceeded");
        openInterestPerUser[_sender] = _openInterestPerUser + _amount;

        // check and increase openInterestPerAssetPerSide
        uint256 _openInterestPerAssetPerSide = openInterestPerAssetPerSide[_tokenId][_isLong];
        require(
            _openInterestPerAssetPerSide + _amount <= maxOpenInterestPerAssetPerSide[_tokenId][_isLong],
            "asset side maxOI exceeded"
        );
        openInterestPerAssetPerSide[_tokenId][_isLong] = _openInterestPerAssetPerSide + _amount;

        // check and increase totalOpenInterest
        uint256 _totalOpenInterest = totalOpenInterest + _amount;
        require(_totalOpenInterest <= maxTotalOpenInterest, "maxTotalOpenInterest exceeded");
        totalOpenInterest = _totalOpenInterest;

        emit IncreaseOpenInterest(_tokenId, _isLong, _amount);
    }

    function decreaseOpenInterest(
        uint256 _tokenId,
        address _sender,
        bool _isLong,
        uint256 _amount
    ) external override onlyVault {
        uint256 _openInterestPerUser = openInterestPerUser[_sender];
        if (_openInterestPerUser < _amount) {
            openInterestPerUser[_sender] = 0;
        } else {
            openInterestPerUser[_sender] = _openInterestPerUser - _amount;
        }

        uint256 _openInterestPerAssetPerSide = openInterestPerAssetPerSide[_tokenId][_isLong];
        if (_openInterestPerAssetPerSide < _amount) {
            openInterestPerAssetPerSide[_tokenId][_isLong] = 0;
        } else {
            openInterestPerAssetPerSide[_tokenId][_isLong] = _openInterestPerAssetPerSide - _amount;
        }

        uint256 _totalOpenInterest = totalOpenInterest;
        if (_totalOpenInterest < _amount) {
            totalOpenInterest = 0;
        } else {
            totalOpenInterest = _totalOpenInterest - _amount;
        }

        emit DecreaseOpenInterest(_tokenId, _isLong, _amount);
    }

    /* ========== MARKET ORDER ========== */
    /* OP FUNCTIONS */
    function setMarketOrderGasFee(uint256 _fee) external onlyOperator(3) {
        require(_fee <= MAX_MARKET_ORDER_GAS_FEE, "Above max");
        marketOrderGasFee = _fee;
        emit SetMarketOrderGasFee(_fee);
    }

    function setExpiryDuration(uint256 _expiryDuration) external onlyOperator(3) {
        require(_expiryDuration <= MAX_EXPIRY_DURATION, "Above max");
        expiryDuration = _expiryDuration;
        emit SetExpiryDuration(_expiryDuration);
    }

    function setSelfExecuteCooldown(uint256 _selfExecuteCooldown) external onlyOperator(3) {
        require(_selfExecuteCooldown <= MAX_SELF_EXECUTE_COOLDOWN, "Above max");
        selfExecuteCooldown = _selfExecuteCooldown;
        emit SetSelfExecuteCooldown(_selfExecuteCooldown);
    }

    /* ========== TRIGGER ORDER ========== */
    /* OP FUNCTIONS */
    function setTriggerGasFee(uint256 _fee) external onlyOperator(3) {
        require(_fee <= MAX_TRIGGER_GAS_FEE, "Above max");
        triggerGasFee = _fee;
        emit SetTriggerGasFee(_fee);
    }

    function setMaxTriggerPerPosition(uint256 _value) external onlyOperator(3) {
        maxTriggerPerPosition = _value;
        emit SetMaxTriggerPerPosition(_value);
    }

    function setPriceMovementPercent(uint256 _priceMovementPercent) external onlyOperator(3) {
        require(_priceMovementPercent <= MAX_PRICE_MOVEMENT_PERCENT, "Above max");
        priceMovementPercent = _priceMovementPercent;
        emit SetPriceMovementPercent(_priceMovementPercent);
    }

    /* ========== ARTIFICIAL SLIPPAGE MECHANISM ========== */
    /* OP FUNCTIONS */
    function setSlippageFactor(uint256 _tokenId, uint256 _slippageFactor) external onlyOperator(3) {
        require(_slippageFactor <= BASIS_POINTS_DIVISOR, "Above max");
        slippageFactor[_tokenId] = _slippageFactor;
        emit SetSlippageFactor(_tokenId, _slippageFactor);
    }

    /* VIEW FUNCTIONS */
    function getPriceWithSlippage(
        uint256 _tokenId,
        bool _isLong,
        uint256 _size,
        uint256 _price
    ) external view override returns (uint256) {
        uint256 _slippageFactor = slippageFactor[_tokenId];

        if (_slippageFactor == 0) return _price;

        uint256 slippage = getSlippage(_slippageFactor, _size);

        return
            _isLong
                ? (_price * (BASIS_POINTS_DIVISOR + slippage)) / BASIS_POINTS_DIVISOR
                : (_price * (BASIS_POINTS_DIVISOR - slippage)) / BASIS_POINTS_DIVISOR;
    }

    function getSlippage(uint256 _slippageFactor, uint256 _size) public view returns (uint256) {
        return (_slippageFactor * (2 * totalOpenInterest + _size)) / (2 * positionVault.getVaultUSDBalance());
    }

    /* ========== LIQUIDATE MECHANISM ========== */
    /* OP FUNCTIONS */
    // the liquidateThreshold should range between 80% to 100%
    function setLiquidateThreshold(uint256 _tokenId, uint256 _liquidateThreshold) external onlyOperator(3) {
        require(
            _liquidateThreshold >= 8 * BASIS_POINTS_DIVISOR && _liquidateThreshold <= LIQUIDATE_THRESHOLD_DIVISOR,
            "Out of range"
        );
        liquidateThreshold[_tokenId] = _liquidateThreshold;
        emit SetLiquidateThreshold(_tokenId, _liquidateThreshold);
    }

    function setLiquidationPendingTime(uint256 _liquidationPendingTime) external onlyOperator(3) {
        require(_liquidationPendingTime <= 60, "Above max");
        liquidationPendingTime = _liquidationPendingTime;
        emit SetLiquidationPendingTime(_liquidationPendingTime);
    }

    function setBountyPercent(
        uint32 _bountyPercentFirstCaller,
        uint32 _bountyPercentResolver
    ) external onlyOperator(3) {
        require(_bountyPercentFirstCaller + _bountyPercentResolver <= BASIS_POINTS_DIVISOR, "invalid bountyPercent");
        bountyPercent_.firstCaller = _bountyPercentFirstCaller;
        bountyPercent_.resolver = _bountyPercentResolver;
        emit SetBountyPercent(_bountyPercentFirstCaller, _bountyPercentResolver);
    }

    /* VIEW FUNCTIONS */
    function bountyPercent() external view override returns (uint32, uint32) {
        return (bountyPercent_.firstCaller, bountyPercent_.resolver);
    }

    /* ========== DELEGATE MECHANISM========== */
    /* USER FUNCTIONS */
    function delegate(address[] memory _delegates) external {
        for (uint256 i = 0; i < _delegates.length; ++i) {
            EnumerableSetUpgradeable.add(_delegatesByMaster[msg.sender], _delegates[i]);
        }
    }

    function undelegate(address[] memory _delegates) external {
        for (uint256 i = 0; i < _delegates.length; ++i) {
            EnumerableSetUpgradeable.remove(_delegatesByMaster[msg.sender], _delegates[i]);
        }
    }

    /* OP FUNCTIONS */
    function setGlobalDelegates(address _delegate, bool _allowed) external onlyOperator(2) {
        globalDelegates[_delegate] = _allowed;
        emit GlobalDelegatesChange(_delegate, _allowed);
    }

    /* VIEW FUNCTIONS */
    function getDelegates(address _master) external view override returns (address[] memory) {
        return enumerate(_delegatesByMaster[_master]);
    }

    function checkDelegation(address _master, address _delegate) public view override returns (bool) {
        require(!checkBanList(_master), "account banned");
        return
            _master == _delegate ||
            globalDelegates[_delegate] ||
            EnumerableSetUpgradeable.contains(_delegatesByMaster[_master], _delegate);
    }

    /* ========== BAN MECHANISM========== */
    /* OP FUNCTIONS */
    function addWalletsToBanList(address[] memory _wallets) external onlyOperator(1) {
        for (uint256 i = 0; i < _wallets.length; ++i) {
            EnumerableSetUpgradeable.add(banWalletList, _wallets[i]);
        }
    }

    function removeWalletsFromBanList(address[] memory _wallets) external onlyOperator(1) {
        for (uint256 i = 0; i < _wallets.length; ++i) {
            EnumerableSetUpgradeable.remove(banWalletList, _wallets[i]);
        }
    }

    /* VIEW FUNCTIONS */
    function checkBanList(address _addr) public view override returns (bool) {
        return EnumerableSetUpgradeable.contains(banWalletList, _addr);
    }

    function enumerate(EnumerableSetUpgradeable.AddressSet storage set) internal view returns (address[] memory) {
        uint256 length = EnumerableSetUpgradeable.length(set);
        address[] memory output = new address[](length);
        for (uint256 i; i < length; ++i) {
            output[i] = EnumerableSetUpgradeable.at(set, i);
        }
        return output;
    }
}

